// src/components/iceland-trip/EnhancedWeatherWidget.js
import React, { useState, useEffect } from 'react';
import { 
  CloudSnow, Wind, Droplets, Thermometer, Sun, Sunset, Eye, 
  Umbrella, AlertTriangle, CloudRain, Clock, ArrowDown, ArrowUp,
  Compass, Activity, Navigation, Gauge, Calendar, ChevronRight, ChevronLeft,
  CheckCircle
} from 'lucide-react';

const EnhancedWeatherWidget = ({ theme }) => {
  const [currentWeather, setCurrentWeather] = useState(null);
  const [forecast, setForecast] = useState(null);
  const [alerts, setAlerts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeHourlyPage, setActiveHourlyPage] = useState(0);
  const [activeTab, setActiveTab] = useState('current'); // 'current', 'hourly', 'daily', 'alerts'
  
  // For travel locations (key points from the itinerary)
  const travelLocations = [
    { name: "雷克雅維克", lat: 64.1466, lon: -21.9426 },
    { name: "教會山", lat: 64.927, lon: -23.308 },
    { name: "黃金圈", lat: 64.255, lon: -21.130 },
    { name: "藍湖", lat: 63.880, lon: -22.449 },
    { name: "冰河湖", lat: 64.047, lon: -16.181 }
  ];
  
  const [selectedLocation, setSelectedLocation] = useState(travelLocations[0]);

  useEffect(() => {
    fetchWeatherData();
  }, [selectedLocation]);

// 在 EnhancedWeatherWidget.js 文件中替換 fetchWeatherData 函數

// 在 EnhancedWeatherWidget.js 文件中替換 fetchWeatherData 函數

const fetchWeatherData = async () => {
  try {
    setLoading(true);
    const apiKey = process.env.REACT_APP_OPENWEATHER_API_KEY;
    
    // 使用當前天氣 API (免費可用)
    const currentRes = await fetch(`https://api.openweathermap.org/data/2.5/weather?lat=${selectedLocation.lat}&lon=${selectedLocation.lon}&units=metric&appid=${apiKey}`);
    
    // 使用 5-day/3-hour forecast API (免費可用) 代替 OneCall API
    const forecastRes = await fetch(`https://api.openweathermap.org/data/2.5/forecast?lat=${selectedLocation.lat}&lon=${selectedLocation.lon}&units=metric&appid=${apiKey}`);
    
    // 空氣品質 API
    const airQualityRes = await fetch(`https://api.openweathermap.org/data/2.5/air_pollution?lat=${selectedLocation.lat}&lon=${selectedLocation.lon}&appid=${apiKey}`);
    
    if (!currentRes.ok) {
      console.error("Current weather API failed:", currentRes.status, currentRes.statusText);
      throw new Error(`Current weather API error: ${currentRes.status}`);
    }
    
    if (!forecastRes.ok) {
      console.error("Forecast API failed:", forecastRes.status, forecastRes.statusText);
      throw new Error(`Forecast API error: ${forecastRes.status}`);
    }
    
    if (!airQualityRes.ok) {
      console.error("Air quality API failed:", airQualityRes.status, airQualityRes.statusText);
      throw new Error(`Air quality API error: ${airQualityRes.status}`);
    }
    
    const currentData = await currentRes.json();
    const forecastData = await forecastRes.json();
    const airQualityData = await airQualityRes.json();
    
    // 處理當前天氣數據
    setCurrentWeather({
      ...currentData,
      airQuality: airQualityData.list[0],
      main: {
        ...currentData.main,
        sunrise: currentData.sys.sunrise,
        sunset: currentData.sys.sunset,
        uvi: 0, // 当前天氣 API 沒有 UV 指數，設為 0
        visibility: currentData.visibility
      }
    });
    
    // 處理 5-day/3-hour forecast API 的資料
    // 這個 API 每 3 小時返回一次預報，共 5 天 (約 40 個時間點)
    
    // 處理小時預報 (取前 24 小時 / 8 個時間點)
    const hourlyForecast = forecastData.list.slice(0, 8).map(item => ({
      dt: item.dt,
      temp: item.main.temp,
      feels_like: item.main.feels_like,
      humidity: item.main.humidity,
      weather: item.weather,
      pop: item.pop || 0, // 降水概率
      wind_speed: item.wind.speed
    }));
    
    // 處理日預報 (每天取中午的預報作為代表)
    const dailyForecast = [];
    const groupedByDay = {};
    
    // 以日期為鍵分組
    forecastData.list.forEach(item => {
      const date = new Date(item.dt * 1000).toLocaleDateString();
      if (!groupedByDay[date]) {
        groupedByDay[date] = [];
      }
      groupedByDay[date].push(item);
    });
    
    // 每天取中午的預報
    Object.entries(groupedByDay).forEach(([date, items], index) => {
      if (index < 7) { // 最多取 7 天
        // 尋找最接近中午的預報
        const noon = new Date(date);
        noon.setHours(12, 0, 0, 0);
        const noonTimestamp = noon.getTime() / 1000;
        
        // 找到最接近中午的預報
        const closestToDayItem = items.reduce((prev, curr) => {
          return Math.abs(curr.dt - noonTimestamp) < Math.abs(prev.dt - noonTimestamp) ? curr : prev;
        }, items[0]);
        
        // 計算溫度範圍
        const temps = items.map(item => item.main.temp);
        const maxTemp = Math.max(...temps);
        const minTemp = Math.min(...temps);
        
        // 計算最大降水概率
        const maxPop = Math.max(...items.map(item => item.pop || 0));
        
        dailyForecast.push({
          dt: closestToDayItem.dt,
          temp: {
            day: closestToDayItem.main.temp,
            min: minTemp,
            max: maxTemp
          },
          weather: closestToDayItem.weather,
          pop: maxPop, // 使用當天最大降水概率
          wind_speed: closestToDayItem.wind.speed
        });
      }
    });
    
    setForecast({
      hourly: hourlyForecast,
      daily: dailyForecast
    });
    
    // 由於我們沒有使用 OneCall API，沒有警報數據
    setAlerts([]);
    
    setError(null);
  } catch (err) {
    console.error("Failed to fetch weather data:", err);
    setError(`無法獲取天氣數據: ${err.message}`);
  } finally {
    setLoading(false);
  }
};  // Helper function to get weather icon
  const getWeatherIcon = (iconCode) => {
    return `http://openweathermap.org/img/wn/${iconCode}@2x.png`;
  };
  
  // Format time from Unix timestamp
  const formatTime = (timestamp) => {
    const date = new Date(timestamp * 1000);
    return date.toLocaleTimeString('zh-TW', { hour: '2-digit', minute: '2-digit' });
  };
  
  // Format day from Unix timestamp
  const formatDay = (timestamp) => {
    const date = new Date(timestamp * 1000);
    const days = ['週日', '週一', '週二', '週三', '週四', '週五', '週六'];
    return days[date.getDay()];
  };
  
  // Get AQI text based on index
  const getAQIText = (aqi) => {
    const aqiTexts = ['優', '良', '中等', '較差', '很差', '危害'];
    return aqiTexts[aqi - 1] || '未知';
  };
  
  // Get AQI color based on index
  const getAQIColor = (aqi, isDarkMode) => {
    const colors = {
      1: isDarkMode ? 'text-green-300' : 'text-green-600',
      2: isDarkMode ? 'text-green-400' : 'text-green-500',
      3: isDarkMode ? 'text-yellow-300' : 'text-yellow-600',
      4: isDarkMode ? 'text-orange-300' : 'text-orange-600',
      5: isDarkMode ? 'text-red-300' : 'text-red-600'
    };
    return colors[aqi] || (isDarkMode ? 'text-gray-300' : 'text-gray-600');
  };
  
  // Get UV Index text based on value
  const getUVIText = (uvi) => {
    if (uvi < 3) return '低';
    if (uvi < 6) return '中等';
    if (uvi < 8) return '高';
    if (uvi < 11) return '很高';
    return '極高';
  };
  
  // Get UV Index color based on value
  const getUVIColor = (uvi, isDarkMode) => {
    if (uvi < 3) return isDarkMode ? 'text-green-300' : 'text-green-600';
    if (uvi < 6) return isDarkMode ? 'text-yellow-300' : 'text-yellow-600';
    if (uvi < 8) return isDarkMode ? 'text-orange-300' : 'text-orange-600';
    if (uvi < 11) return isDarkMode ? 'text-red-300' : 'text-red-600';
    return isDarkMode ? 'text-purple-300' : 'text-purple-600';
  };
  
  // Get wind direction as text
  const getWindDirection = (deg) => {
    const directions = ['北', '東北', '東', '東南', '南', '西南', '西', '西北'];
    return directions[Math.round(deg / 45) % 8];
  };
  
  // Render current weather section
  const renderCurrentWeather = () => {
    if (!currentWeather) return null;
    
    const weather = currentWeather.weather[0];
    const main = currentWeather.main;
    const wind = currentWeather.wind;
    const airQuality = currentWeather.airQuality;
    const isDarkMode = theme.darkMode;
    
    return (
      <div className="space-y-4">
        {/* Main weather display */}
        <div className="flex justify-between items-center">
          <div className="flex items-center">
            <img 
              src={getWeatherIcon(weather.icon)} 
              alt={weather.description}
              className="w-20 h-20"
            />
            <div>
              <div className="text-3xl font-bold">{Math.round(main.temp)}°C</div>
              <div className="capitalize">{weather.description}</div>
            </div>
          </div>
          
          <div>
            <div className="flex items-center">
              <ArrowUp className={`h-4 w-4 ${theme.highlight} mr-1`} />
              <span className="mr-2">{Math.round(main.temp_max)}°</span>
              <ArrowDown className={`h-4 w-4 ${theme.highlight} mr-1`} />
              <span>{Math.round(main.temp_min)}°</span>
            </div>
            <div className="text-sm mt-1">體感溫度: {Math.round(main.feels_like)}°C</div>
          </div>
        </div>
        
        {/* Weather details grid */}
        <div className="grid grid-cols-2 md:grid-cols-4 gap-2">
          {/* Wind */}
          <div className={`${theme.cardBg} p-3 rounded-lg`}>
            <div className="flex items-center mb-1">
              <Wind className={`mr-1 h-4 w-4 ${theme.highlight}`} />
              <div className="text-sm font-medium">風速</div>
            </div>
            <div className="flex justify-between items-center">
              <div>{wind.speed} m/s</div>
              <div className="flex items-center text-sm">
                <Navigation className={`h-3 w-3 mr-1 ${theme.highlight}`} style={{ transform: `rotate(${wind.deg}deg)` }} />
                <span>{getWindDirection(wind.deg)}</span>
              </div>
            </div>
          </div>
          
          {/* Humidity */}
          <div className={`${theme.cardBg} p-3 rounded-lg`}>
            <div className="flex items-center mb-1">
              <Droplets className={`mr-1 h-4 w-4 ${theme.highlight}`} />
              <div className="text-sm font-medium">濕度</div>
            </div>
            <div className="flex justify-between items-center">
              <div>{main.humidity}%</div>
              <div className="w-16 h-2 bg-gray-200 rounded-full overflow-hidden">
                <div 
                  className={`h-full ${isDarkMode ? 'bg-blue-400' : 'bg-blue-600'}`} 
                  style={{ width: `${main.humidity}%` }}
                ></div>
              </div>
            </div>
          </div>
          
          {/* Pressure */}
          <div className={`${theme.cardBg} p-3 rounded-lg`}>
            <div className="flex items-center mb-1">
              <Gauge className={`mr-1 h-4 w-4 ${theme.highlight}`} />
              <div className="text-sm font-medium">氣壓</div>
            </div>
            <div>{main.pressure} hPa</div>
          </div>
          
          {/* Visibility */}
          <div className={`${theme.cardBg} p-3 rounded-lg`}>
            <div className="flex items-center mb-1">
              <Eye className={`mr-1 h-4 w-4 ${theme.highlight}`} />
              <div className="text-sm font-medium">能見度</div>
            </div>
            <div>{(main.visibility / 1000).toFixed(1)} km</div>
          </div>
        </div>
        
        {/* Sun and UV index */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          {/* Sunrise/Sunset */}
          <div className={`${theme.cardBg} p-3 rounded-lg`}>
            <div className="flex items-center justify-between">
              <div className="flex items-center">
                <Sun className={`mr-1 h-4 w-4 ${isDarkMode ? 'text-amber-300' : 'text-amber-500'}`} />
                <div className="text-sm">日出 {formatTime(main.sunrise)}</div>
              </div>
              <div className="flex items-center">
                <Sunset className={`mr-1 h-4 w-4 ${isDarkMode ? 'text-orange-300' : 'text-orange-500'}`} />
                <div className="text-sm">日落 {formatTime(main.sunset)}</div>
              </div>
            </div>
            <div className="mt-2 relative h-3 bg-gray-200 rounded-full overflow-hidden">
              <div 
                className="h-full bg-gradient-to-r from-amber-400 via-yellow-300 to-orange-400" 
                style={{ width: '100%' }}
              ></div>
              
              {/* Current time marker */}
              {(() => {
                const now = Math.floor(Date.now() / 1000);
                const sunrise = main.sunrise;
                const sunset = main.sunset;
                const dayLength = sunset - sunrise;
                const position = ((now - sunrise) / dayLength) * 100;
                
                // Only show if it's daytime
                if (now >= sunrise && now <= sunset) {
                  return (
                    <div 
                      className="absolute top-0 w-1 h-3 bg-red-500"
                      style={{ left: `${position}%` }}
                    ></div>
                  );
                }
                return null;
              })()}
            </div>
          </div>
          
          {/* UV Index */}
          <div className={`${theme.cardBg} p-3 rounded-lg`}>
            <div className="flex items-center mb-1">
              <Activity className={`mr-1 h-4 w-4 ${theme.highlight}`} />
              <div className="text-sm font-medium">紫外線指數</div>
            </div>
            <div className="flex justify-between items-center">
              <div className="flex items-center">
                <div className={`text-lg font-bold ${getUVIColor(main.uvi, isDarkMode)}`}>
                  {Math.round(main.uvi)}
                </div>
                <div className={`ml-1 text-sm ${getUVIColor(main.uvi, isDarkMode)}`}>
                  ({getUVIText(main.uvi)})
                </div>
              </div>
              <div className="w-24 h-2 bg-gray-200 rounded-full overflow-hidden">
                <div 
                  className="h-full bg-gradient-to-r from-green-400 via-yellow-400 to-red-500" 
                  style={{ width: `${Math.min(main.uvi * 9, 100)}%` }}
                ></div>
              </div>
            </div>
          </div>
        </div>
        
        {/* Air Quality */}
        {airQuality && (
          <div className={`${theme.cardBg} p-3 rounded-lg`}>
            <div className="flex items-center justify-between mb-2">
              <div className="flex items-center">
                <Wind className={`mr-1 h-4 w-4 ${theme.highlight}`} />
                <div className="text-sm font-medium">空氣品質</div>
              </div>
              <div className={`text-sm font-medium ${getAQIColor(airQuality.main.aqi, isDarkMode)}`}>
                {getAQIText(airQuality.main.aqi)}
              </div>
            </div>
            <div className="grid grid-cols-3 gap-2 text-xs">
              <div>
                <div className={`font-medium ${theme.secondaryText}`}>PM2.5</div>
                <div>{airQuality.components.pm2_5.toFixed(1)}</div>
              </div>
              <div>
                <div className={`font-medium ${theme.secondaryText}`}>PM10</div>
                <div>{airQuality.components.pm10.toFixed(1)}</div>
              </div>
              <div>
                <div className={`font-medium ${theme.secondaryText}`}>NO2</div>
                <div>{airQuality.components.no2.toFixed(1)}</div>
              </div>
            </div>
          </div>
        )}
      </div>
    );
  };
  
  // Render hourly forecast section
  const renderHourlyForecast = () => {
    if (!forecast || !forecast.hourly) return null;
    
    const ITEMS_PER_PAGE = 6;
    const totalPages = Math.ceil(forecast.hourly.length / ITEMS_PER_PAGE);
    const startIdx = activeHourlyPage * ITEMS_PER_PAGE;
    const endIdx = startIdx + ITEMS_PER_PAGE;
    const currentItems = forecast.hourly.slice(startIdx, endIdx);
    
    return (
      <div className="space-y-4">
        <div className="flex items-center justify-between mb-2">
          <h3 className="text-lg font-medium">每小時預報</h3>
          
          {/* Pagination controls */}
          <div className="flex items-center space-x-2">
            <button 
              onClick={() => setActiveHourlyPage(prev => Math.max(0, prev - 1))}
              disabled={activeHourlyPage === 0}
              className={`p-1 rounded ${activeHourlyPage === 0 ? 'opacity-50 cursor-not-allowed' : 'hover:bg-gray-200'}`}
            >
              <ChevronLeft className="h-5 w-5" />
            </button>
            <span className="text-sm">
              {activeHourlyPage + 1}/{totalPages}
            </span>
            <button 
              onClick={() => setActiveHourlyPage(prev => Math.min(totalPages - 1, prev + 1))}
              disabled={activeHourlyPage === totalPages - 1}
              className={`p-1 rounded ${activeHourlyPage === totalPages - 1 ? 'opacity-50 cursor-not-allowed' : 'hover:bg-gray-200'}`}
            >
              <ChevronRight className="h-5 w-5" />
            </button>
          </div>
        </div>
        
        <div className="grid grid-cols-6 gap-2">
          {currentItems.map((hour, idx) => (
            <div key={idx} className={`${theme.cardBg} p-2 rounded-lg text-center`}>
              <div className="text-sm font-medium">
                {formatTime(hour.dt)}
              </div>
              <img 
                src={getWeatherIcon(hour.weather[0].icon)} 
                alt={hour.weather[0].description}
                className="w-10 h-10 mx-auto"
              />
              <div className="text-lg font-bold">{Math.round(hour.temp)}°</div>
              <div className="flex items-center justify-center text-xs">
                <CloudRain className="h-3 w-3 mr-1" />
                <span>{Math.round(hour.pop * 100)}%</span>
              </div>
            </div>
          ))}
        </div>
        
        {/* Precipitation and temperature trend chart */}
        <div className={`${theme.cardBg} p-3 rounded-lg mt-4`}>
          <div className="text-sm font-medium mb-2">溫度與降水趨勢</div>
          <div className="relative h-32">
            {/* Temperature line */}
            <svg className="w-full h-full" preserveAspectRatio="none">
              <defs>
                <linearGradient id="tempGradient" x1="0%" y1="0%" x2="0%" y2="100%">
                  <stop offset="0%" stopColor={theme.darkMode ? "#ef4444" : "#dc2626"} stopOpacity="0.5" />
                  <stop offset="100%" stopColor={theme.darkMode ? "#3b82f6" : "#2563eb"} stopOpacity="0.5" />
                </linearGradient>
              </defs>
              
              {/* Draw temperature line */}
              {(() => {
                const items = forecast.hourly.slice(0, 24);
                const temps = items.map(h => h.temp);
                const minTemp = Math.min(...temps) - 2;
                const maxTemp = Math.max(...temps) + 2;
                const tempRange = maxTemp - minTemp;
                
                // Create polyline points
                const points = items.map((hour, i) => {
                  const x = (i / (items.length - 1)) * 100;
                  const y = 100 - ((hour.temp - minTemp) / tempRange) * 100;
                  return `${x},${y}`;
                }).join(' ');
                
                return (
                  <polyline
                    fill="none"
                    stroke={theme.darkMode ? "#f97316" : "#ea580c"}
                    strokeWidth="2"
                    points={points}
                  />
                );
              })()}
            </svg>
            
            {/* Precipitation bars overlay */}
            <div className="absolute inset-0 flex items-end">
              {forecast.hourly.slice(0, 24).map((hour, i) => {
                const precipHeight = hour.pop * 100;
                return (
                  <div 
                    key={i} 
                    className="flex-1 flex items-end justify-center"
                  >
                    <div 
                      className={`w-4/5 ${theme.darkMode ? 'bg-blue-500' : 'bg-blue-400'} opacity-60 rounded-t`}
                      style={{ height: `${precipHeight}%` }}
                    ></div>
                  </div>
                );
              })}
            </div>
            
            {/* Legend */}
            <div className="absolute top-0 right-0 flex items-center text-xs">
              <div className="flex items-center mr-2">
                <div className={`w-3 h-1 ${theme.darkMode ? 'bg-orange-400' : 'bg-orange-600'} mr-1`}></div>
                <span>溫度</span>
              </div>
              <div className="flex items-center">
                <div className={`w-2 h-3 ${theme.darkMode ? 'bg-blue-500' : 'bg-blue-400'} opacity-60 mr-1`}></div>
                <span>降水機率</span>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  };
  
  // Render daily forecast section
  const renderDailyForecast = () => {
    if (!forecast || !forecast.daily) return null;
    
    return (
      <div className="space-y-4">
        <h3 className="text-lg font-medium mb-2">未來七天預報</h3>
        
        <div className="space-y-2">
          {forecast.daily.map((day, idx) => (
            <div key={idx} className={`${theme.cardBg} p-3 rounded-lg flex items-center justify-between`}>
              <div className="flex items-center">
                <div className="w-16">
                  {idx === 0 ? '今天' : formatDay(day.dt)}
                </div>
                <img 
                  src={getWeatherIcon(day.weather[0].icon)} 
                  alt={day.weather[0].description}
                  className="w-10 h-10"
                />
                <div className="ml-1">
                  <div className="text-sm capitalize">{day.weather[0].description}</div>
                </div>
              </div>
              
              <div className="flex items-center space-x-4">
                <div className="flex items-center">
                  <CloudRain className="h-4 w-4 mr-1" />
                  <span>{Math.round(day.pop * 100)}%</span>
                </div>
                <div className="flex items-center space-x-1">
                  <ArrowDown className={`h-4 w-4 ${theme.darkMode ? 'text-blue-400' : 'text-blue-600'}`} />
                  <span>{Math.round(day.temp.min)}°</span>
                  <ArrowUp className={`h-4 w-4 ${theme.darkMode ? 'text-red-400' : 'text-red-600'} ml-1`} />
                  <span>{Math.round(day.temp.max)}°</span>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  };
  
  // Render weather alerts section
  const renderWeatherAlerts = () => {
    if (!alerts || alerts.length === 0) {
      return (
        <div className={`${theme.cardBg} p-4 rounded-lg text-center`}>
          <div className="flex justify-center mb-2">
            <div className={`rounded-full p-2 ${theme.darkMode ? 'bg-green-900 bg-opacity-30' : 'bg-green-100'}`}>
              <CheckCircle className={`h-6 w-6 ${theme.darkMode ? 'text-green-400' : 'text-green-600'}`} />
            </div>
          </div>
          <p>目前沒有活躍的天氣警報</p>
          <p className="text-sm mt-1">旅程順利！</p>
        </div>
      );
    }
    
    return (
      <div className="space-y-4">
        <h3 className="text-lg font-medium mb-2">天氣警報</h3>
        
        {alerts.map((alert, idx) => {
          const startTime = new Date(alert.start * 1000);
          const endTime = new Date(alert.end * 1000);
          
          return (
            <div 
              key={idx} 
              className={`${theme.darkMode ? 'bg-red-900 bg-opacity-20' : 'bg-red-50'} p-3 rounded-lg border ${theme.darkMode ? 'border-red-500 border-opacity-30' : 'border-red-300'}`}
            >
              <div className="flex items-start">
                <AlertTriangle className={`h-5 w-5 ${theme.darkMode ? 'text-red-400' : 'text-red-600'} mt-0.5 mr-2 flex-shrink-0`} />
                <div>
                  <div className="font-bold">{alert.event}</div>
                  <div className="text-sm mt-1">{alert.description}</div>
                  <div className={`text-xs mt-2 ${theme.secondaryText}`}>
                    <div className="flex items-center">
                      <Clock className="h-3 w-3 mr-1" />
                      <span>
                        {startTime.toLocaleDateString('zh-TW')} {startTime.toLocaleTimeString('zh-TW', { hour: '2-digit', minute: '2-digit' })} 
                        {' - '}
                        {endTime.toLocaleDateString('zh-TW')} {endTime.toLocaleTimeString('zh-TW', { hour: '2-digit', minute: '2-digit' })}
                      </span>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          );
        })}
      </div>
    );
  };
  
  return (
    <div className={`mb-6 ${theme.cardBg} rounded-xl p-4 ${theme.cardBorder}`}>
      {/* Header */}
      <div className="flex justify-between items-center mb-4">
        <div className="flex items-center gap-2">
          <CloudSnow className={`h-5 w-5 ${theme.highlight}`} />
          <h2 className="text-lg font-semibold">冰島天氣預報</h2>
        </div>
        
        {/* Location Selector */}
        <select
          value={JSON.stringify(selectedLocation)}
          onChange={(e) => setSelectedLocation(JSON.parse(e.target.value))}
          className={`text-sm px-2 py-1 rounded ${theme.darkMode ? 'bg-blue-800 bg-opacity-50 text-white border-blue-600' : 'bg-white border-blue-300'} border`}
        >
          {travelLocations.map((loc, idx) => (
            <option key={idx} value={JSON.stringify(loc)}>
              {loc.name}
            </option>
          ))}
        </select>
      </div>
      
      {/* Tab Navigation */}
      <div className="flex border-b mb-4 overflow-x-auto pb-1">
        <button
          onClick={() => setActiveTab('current')}
          className={`px-3 py-2 text-sm font-medium ${
            activeTab === 'current' 
              ? `${theme.darkMode ? 'text-white' : 'text-blue-600'} border-b-2 ${theme.darkMode ? 'border-blue-400' : 'border-blue-600'}` 
              : theme.secondaryText
          }`}
        >
          即時天氣
        </button>
        <button
          onClick={() => setActiveTab('hourly')}
          className={`px-3 py-2 text-sm font-medium ${
            activeTab === 'hourly' 
              ? `${theme.darkMode ? 'text-white' : 'text-blue-600'} border-b-2 ${theme.darkMode ? 'border-blue-400' : 'border-blue-600'}` 
              : theme.secondaryText
          }`}
        >
          小時預報
        </button>
        <button
          onClick={() => setActiveTab('daily')}
          className={`px-3 py-2 text-sm font-medium ${
            activeTab === 'daily' 
              ? `${theme.darkMode ? 'text-white' : 'text-blue-600'} border-b-2 ${theme.darkMode ? 'border-blue-400' : 'border-blue-600'}` 
              : theme.secondaryText
          }`}
        >
          每日預報
        </button>
        <button
          onClick={() => setActiveTab('alerts')}
          className={`px-3 py-2 text-sm font-medium ${
            activeTab === 'alerts' 
              ? `${theme.darkMode ? 'text-white' : 'text-blue-600'} border-b-2 ${theme.darkMode ? 'border-blue-400' : 'border-blue-600'}` 
              : theme.secondaryText
          }`}
        >
          天氣警報
        </button>
      </div>
      
      {/* Loading State */}
      {loading ? (
        <div className="flex flex-col items-center justify-center py-8">
          <div className={`animate-spin h-10 w-10 border-4 ${theme.darkMode ? 'border-blue-300' : 'border-blue-600'} rounded-full border-t-transparent`}></div>
          <div className="mt-4">正在載入天氣資料...</div>
        </div>
      ) : error ? (
        <div className="text-center py-6">
          <AlertTriangle className={`h-10 w-10 ${theme.darkMode ? 'text-red-400' : 'text-red-600'} mx-auto mb-2`} />
          <p className={theme.darkMode ? 'text-red-300' : 'text-red-600'}>
            {error}
          </p>
          <button
            onClick={fetchWeatherData}
            className={`mt-4 px-4 py-2 ${theme.button} text-white rounded-lg`}
          >
            重新載入
          </button>
        </div>
      ) : (
        <div>
          {activeTab === 'current' && renderCurrentWeather()}
          {activeTab === 'hourly' && renderHourlyForecast()}
          {activeTab === 'daily' && renderDailyForecast()}
          {activeTab === 'alerts' && renderWeatherAlerts()}
        </div>
      )}
      
      {/* Footer with API attribution */}
      <div className={`text-xs ${theme.secondaryText} text-center mt-4`}>
        天氣數據由 OpenWeatherMap 提供
      </div>
    </div>
  );
};

export default EnhancedWeatherWidget;
