                </div>
              </div>
            );
          })}
          
          {/* Legend */}
          <div className="absolute bottom-2 right-2 bg-blue-900 bg-opacity-80 text-xs p-2 rounded">
            <div className="flex items-center mb-1">
              <div className="w-2 h-2 bg-blue-500 rounded-full mr-2"></div>
              <span>行程點</span>
            </div>
            <div className="text-blue-200">總計: {locations.length} 個地點</div>
          </div>
        </div>
      </div>
    );
  };

  // Iceland-inspired color scheme
  const colors = {
    primary: 'bg-blue-600',
    secondary: 'bg-teal-500',
    accent: 'bg-amber-500',
    glacier: 'bg-blue-300',
    lava: 'bg-red-500',
    moss: 'bg-green-700',
    text: 'text-gray-800',
    lightText: 'text-gray-100',
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-900 via-blue-700 to-blue-900 text-white">
      {/* Header with mobile menu */}
      <header className="sticky top-0 z-50 bg-blue-900 bg-opacity-90 backdrop-blur-md shadow-lg">
        <div className="container mx-auto px-4 py-3 flex items-center justify-between">
          <div className="flex items-center space-x-2">
            <Compass className="h-6 w-6 text-blue-300" />
            <h1 className="text-xl font-bold">冰島旅遊指南</h1>
          </div>
          
          <div className="hidden md:flex items-center space-x-6">
            <button 
              onClick={() => setActiveTab('itinerary')} 
              className={`flex items-center space-x-1 ${activeTab === 'itinerary' ? 'text-white' : 'text-blue-300 hover:text-white'}`}>
              <Calendar className="h-4 w-4" />
              <span>行程</span>
            </button>
            <button 
              onClick={() => setActiveTab('info')} 
              className={`flex items-center space-x-1 ${activeTab === 'info' ? 'text-white' : 'text-blue-300 hover:text-white'}`}>
              <Info className="h-4 w-4" />
              <span>實用資訊</span>
            </button>
            <button 
              onClick={() => setActiveTab('packing')} 
              className={`flex items-center space-x-1 ${activeTab === 'packing' ? 'text-white' : 'text-blue-300 hover:text-white'}`}>
              <Bookmark className="h-4 w-4" />
              <span>行前準備</span>
            </button>
          </div>
          
          <button onClick={toggleMenu} className="md:hidden text-white focus:outline-none">
            {isMenuOpen ? <X className="h-6 w-6" /> : <Menu className="h-6 w-6" />}
          </button>
        </div>
        
        {/* Mobile menu */}
        {isMenuOpen && (
          <div className="md:hidden bg-blue-800 bg-opacity-95 backdrop-blur-md">
            <div className="container mx-auto px-4 py-2 space-y-3">
              <button 
                onClick={() => { setActiveTab('itinerary'); toggleMenu(); }} 
                className="flex items-center space-x-2 w-full py-2">
                <Calendar className="h-5 w-5" />
                <span>行程</span>
              </button>
              <button 
                onClick={() => { setActiveTab('info'); toggleMenu(); }} 
                className="flex items-center space-x-2 w-full py-2">
                <Info className="h-5 w-5" />
                <span>實用資訊</span>
              </button>
              <button 
                onClick={() => { setActiveTab('packing'); toggleMenu(); }} 
                className="flex items-center space-x-2 w-full py-2">
                <Bookmark className="h-5 w-5" />
                <span>行前準備</span>
              </button>
            </div>
          </div>
        )}
      </header>

      <main className="container mx-auto px-4 py-6 mb-20">
        {/* Weather Dashboard */}
        <div className="mb-8 bg-white bg-opacity-10 backdrop-blur-md rounded-xl shadow-lg p-4 border border-blue-300 border-opacity-30">
          <h2 className="text-lg font-semibold mb-3 flex items-center gap-2">
            <CloudSnow className="h-5 w-5 text-blue-300" />
            <span>冰島天氣</span>
          </h2>
          
          {weather ? (
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div className="flex items-center space-x-4">
                <div className="text-4xl font-bold">{weather.temperature}°C</div>
                <div>
                  <div className="text-sm uppercase">{weather.condition}</div>
                  <div className="flex items-center space-x-3 mt-2 text-blue-200 text-sm">
                    <div className="flex items-center">
                      <Wind className="h-4 w-4 mr-1" />
                      <span>{weather.windSpeed} km/h</span>
                    </div>
                    <div className="flex items-center">
                      <Droplets className="h-4 w-4 mr-1" />
                      <span>{weather.humidity}%</span>
                    </div>
                    <div className="flex items-center">
                      <Thermometer className="h-4 w-4 mr-1" />
                      <span>體感 {weather.feelsLike}°C</span>
                    </div>
                  </div>
                </div>
              </div>
              
              <div className="flex space-x-2 overflow-x-auto md:justify-end py-2">
                {weatherForecast.map((day, index) => (
                  <div key={index} className="flex flex-col items-center bg-white bg-opacity-10 rounded-lg p-2 min-w-16">
                    <div className="text-xs">{day.day}</div>
                    <div className="text-sm font-semibold">{day.temp}°</div>
                    <div className="text-xs truncate">{day.condition}</div>
                  </div>
                ))}
              </div>
            </div>
          ) : (
            <div className="text-center py-2">
              <span className="text-sm">載入天氣資訊中...</span>
            </div>
          )}
        </div>
        
        {/* Itinerary Tab */}
        {activeTab === 'itinerary' && (
          <>
            {/* Date selector */}
            <div className="mb-6 overflow-x-auto pb-2">
              <div className="flex space-x-2">
                {days.map((day, index) => (
                  <button
                    key={index}
                    onClick={() => setSelectedDay(index + 1)}
                    className={`px-4 py-3 rounded-lg flex-shrink-0 transition-all transform ${
                      selectedDay === index + 1
                        ? 'bg-blue-500 shadow-lg scale-105'
                        : 'bg-white bg-opacity-10 hover:bg-opacity-20'
                    }`}
                  >
                    <div className="text-sm font-medium">{day.date}</div>
                    <div className="text-xs truncate max-w-32">{day.title}</div>
                  </button>
                ))}
              </div>
            </div>

            {/* Current day info */}
            <div className="mb-6 bg-white bg-opacity-10 backdrop-blur-md rounded-xl p-4 border border-blue-300 border-opacity-30">
              <div className="flex items-center gap-3 mb-2">
                <Calendar className="w-5 h-5 text-blue-300" />
                <h3 className="text-xl font-semibold">
                  {days[selectedDay - 1]?.title}
                </h3>
              </div>
              <div className="mt-2 flex items-center gap-4 text-sm text-blue-100">
                <div className="flex items-center gap-1">
                  <Sun className="w-4 h-4 text-amber-400" />
                  <span>日出: {days[selectedDay - 1]?.sunrise}</span>
                </div>
                <div className="flex items-center gap-1">
                  <Sunset className="w-4 h-4 text-amber-500" />
                  <span>日落: {days[selectedDay - 1]?.sunset}</span>
                </div>
              </div>
            </div>

            {/* Map */}
            <div className="mb-6 overflow-hidden rounded-xl shadow-lg">
              {selectedDayLocations.length > 0 ? (
                <div className="relative">
                  <div className="absolute top-0 left-0 right-0 z-10 bg-gradient-to-b from-blue-900 to-transparent h-8 opacity-70"></div>
                  {mapKey ? (
                    <MapComponent locations={selectedDayLocations} />
                  ) : (
                    <FallbackMapComponent locations={selectedDayLocations} />
                  )}
                  <div className="absolute bottom-0 left-0 right-0 z-10 bg-gradient-to-t from-blue-900 to-transparent h-8 opacity-70"></div>
                </div>
              ) : (
                <div className="bg-blue-800 bg-opacity-30 h-64 flex items-center justify-center">
                  <p>此日期無地圖標記點</p>
                </div>
              )}
            </div>

            {/* Activities */}
            <div className="space-y-4">
              <h3 className="text-lg font-semibold flex items-center gap-2 mb-4">
                <FileText className="h-5 w-5 text-blue-300" />
                <span>當日行程</span>
              </h3>
              
              {days[selectedDay - 1]?.activities.map((activity, index) => (
                <div 
                  key={index} 
                  className="bg-white bg-opacity-10 backdrop-blur-md rounded-xl p-4 border border-blue-300 border-opacity-20 hover:bg-opacity-15 transition-all cursor-pointer"
                  onClick={() => openActivityDetails(activity)}
                >
                  <div className="flex items-start gap-3">
                    <MapPin className="w-5 h-5 text-blue-300 mt-1 flex-shrink-0" />
                    <div>
                      <h3 className="font-semibold text-lg">
                        {activity.time && `${activity.time} - `}
                        {activity.location || activity.type}
                      </h3>
                      {activity.transport && (
                        <p className="text-blue-100 text-sm mt-1 flex items-center">
                          <Clock className="w-4 h-4 inline mr-2 text-blue-300" />
                          {activity.transport}
                        </p>
                      )}
                      {activity.duration && (
                        <p className="text-blue-100 text-sm">
                          建議停留：{activity.duration}
                        </p>
                      )}
                    </div>
                  </div>
                </div>
              ))}
            </div>
            
            {/* Notice section */}
            <div className="mt-8 bg-amber-500 bg-opacity-10 backdrop-blur-md rounded-xl p-4 border border-amber-300 border-opacity-30">
              <div className="flex items-center gap-2 mb-3">
                <AlertCircle className="w-5 h-5 text-amber-400" />
                <h3 className="text-lg font-semibold">注意事項</h3>
              </div>
              <ul className="space-y-2 text-sm text-blue-50">
                <li className="flex items-start gap-2">
                  <span className="text-amber-400">•</span>
                  <span>冬季冰島天氣多變，請務必關注天氣預報及路況，並預留充裕的交通時間。</span>
                </li>
                <li className="flex items-start gap-2">
                  <span className="text-amber-400">•</span>
                  <span>景點停留時間為參考建議，您可依照個人興趣及行程彈性調整。</span>
                </li>
                <li className="flex items-start gap-2">
                  <span className="text-amber-400">•</span>
                  <span>冰島自然環境脆弱，請遵守環保原則，不踐踏植被，不丟棄垃圾。</span>
                </li>
              </ul>
            </div>
          </>
        )}
        
        {/* Info Tab */}
        {activeTab === 'info' && (
          <div className="space-y-6">
            <h2 className="text-xl font-semibold flex items-center gap-2 mb-4">
              <BookOpen className="h-6 w-6 text-blue-300" />
              <span>冰島實用資訊</span>
            </h2>
            
            {essentialInfo.map((section, index) => (
              <div key={index} className="bg-white bg-opacity-10 backdrop-blur-md rounded-xl p-5 border border-blue-300 border-opacity-30">
                <h3 className="text-lg font-semibold mb-4">{section.title}</h3>
                <div className="space-y-3">
                  {section.items.map((item, idx) => (
                    <div key={idx} className="flex justify-between items-center border-b border-blue-200 border-opacity-20 pb-2">
                      <span className="text-blue-100">{item.label}</span>
                      <span className="font-medium">{item.value}</span>
                    </div>
                  ))}
                </div>
              </div>
            ))}
            
            <div className="bg-white bg-opacity-10 backdrop-blur-md rounded-xl p-5 border border-blue-300 border-opacity-30">
              <h3 className="text-lg font-semibold mb-4 flex items-center gap-2">
                <Camera className="h-5 w-5 text-blue-300" />
                <span>推薦拍攝點</span>
              </h3>
              
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div className="rounded-lg overflow-hidden">
                  <div className="aspect-video bg-gray-700 flex items-center justify-center">
                    <img src="/api/placeholder/400/225" alt="教會山日落" className="w-full h-full object-cover" />
                  </div>
                  <div className="p-3 bg-blue-900 bg-opacity-50">
                    <h4 className="font-medium">教會山日落</h4>
                    <p className="text-xs text-blue-100">最佳時間: 17:30-18:30</p>
                  </div>
                </div>
                
                <div className="rounded-lg overflow-hidden">
                  <div className="aspect-video bg-gray-700 flex items-center justify-center">
                    <img src="/api/placeholder/400/225" alt="鑽石沙灘" className="w-full h-full object-cover" />
                  </div>
                  <div className="p-3 bg-blue-900 bg-opacity-50">
                    <h4 className="font-medium">鑽石沙灘</h4>
                    <p className="text-xs text-blue-100">最佳時間: 日出後</p>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}
        
        {/* Packing List Tab */}
        {activeTab === 'packing' && (
          <div className="space-y-6">
            <h2 className="text-xl font-semibold flex items-center gap-2 mb-4">
              <Bookmark className="h-6 w-6 text-blue-300" />
              <span>行前準備</span>
            </h2>
            
            <div className="bg-white bg-opacity-10 backdrop-blur-md rounded-xl p-5 border border-blue-300 border-opacity-30">
              <h3 className="text-lg font-semibold mb-4">打包清單</h3>
              
              <div className="space-y-4">
                {['必備文件', '衣物', '電子設備', '其他'].map(category => (
                  <div key={category}>
                    <h4 className="font-medium text-blue-300 mb-2">{category}</h4>
                    <div className="space-y-2">
                      {packingList
                        .filter(item => item.category === category)
                        .map((item, idx) => (
                          <div key={idx} className="flex items-center">
                            <input 
                              type="checkbox" 
                              id={`item-${idx}`} 
                              className="h-5 w-5 rounded border-blue-400 text-blue-600 focus:ring-blue-500"
                            />
                            <label htmlFor={`item-${idx}`} className="ml-3 text-sm">{item.item}</label>
                          </div>
                        ))}
                    </div>
                  </div>
                ))}
              </div>
            </div>
            
            <div className="bg-white bg-opacity-10 backdrop-blur-md rounded-xl p-5 border border-blue-300 border-opacity-30">
              <h3 className="text-lg font-semibold mb-4">冬季冰島旅遊小貼士</h3>
              
              <div className="space-y-3 text-sm">
                <p className="flex items-start gap-2">
                  <span className="text-amber-400 flex-shrink-0">•</span>
                  <span>冬季冰島氣溫可能低至 -10°C，風加上濕度會讓體感更冷，請務必做好保暖準備。</span>
                </p>
                <p className="flex items-start gap-2">
                  <span className="text-amber-400 flex-shrink-0">•</span>
                  <span>日照時間短，通常早上 9 點左右才天亮，下午 4-5 點就會天黑，請合理安排行程。</span>
                </p>
                <p className="flex items-start gap-2">
                  <span className="text-amber-400 flex-shrink-0">•</span>
                  <span>冰島自駕需特別注意冬季路況，請使用官方網站 road.is 查詢即時路況。</span>
                </p>
                <p className="flex items-start gap-2">
                  <span className="text-amber-400 flex-shrink-0">•</span>
                  <span>攝影裝備需防寒保護，相機電池在低溫下消耗快，請準備足夠的備用電池。</span>
                </p>
              </div>
            </div>
          </div>
        )}
      </main>
      
      {/* Bottom navigation for mobile */}
      <nav className="md:hidden fixed bottom-0 left-0 right-0 bg-blue-900 bg-opacity-95 backdrop-blur-md shadow-lg z-40">
        <div className="flex justify-around">
          <button 
            onClick={() => setActiveTab('itinerary')} 
            className={`flex flex-col items-center py-3 px-4 ${activeTab === 'itinerary' ? 'text-white' : 'text-blue-300'}`}
          >
            <Calendar className="h-5 w-5" />
            <span className="text-xs mt-1">行程</span>
          </button>
          <button 
            onClick={() => setActiveTab('info')} 
            className={`flex flex-col items-center py-3 px-4 ${activeTab === 'info' ? 'text-white' : 'text-blue-300'}`}
          >
            <Info className="h-5 w-5" />
            <span className="text-xs mt-1">資訊</span>
          </button>
          <button 
            onClick={() => setActiveTab('packing')} 
            className={`flex flex-col items-center py-3 px-4 ${activeTab === 'packing' ? 'text-white' : 'text-blue-300'}`}
          >
            <Bookmark className="h-5 w-5" />
            <span className="text-xs mt-1">準備</span>
          </button>
        </div>
      </nav>
      
      {/* Activity details modal */}
      {selectedActivity && (
        <div className="fixed inset-0 bg-black bg-opacity-80 flex items-center justify-center z-50 p-4">
          <div className="bg-blue-900 rounded-xl max-w-lg w-full max-h-screen overflow-y-auto">
            <div className="relative">
              <img 
                src={selectedActivity.image} 
                alt={selectedActivity.location || selectedActivity.type} 
                className="w-full h-48 object-cover rounded-t-xl"
              />
              <button 
                onClick={closeActivityDetails}
                className="absolute top-2 right-2 bg-black bg-opacity-50 rounded-full p-1"
              >
                <X className="h-6 w-6" />
              </button>
            </div>
            
            <div className="p-5">
              <h3 className="text-xl font-semibold mb-2">
                {selectedActivity.location || selectedActivity.type}
              </h3>
              
              {selectedActivity.time && (
                <p className="text-blue-200 text-sm">
                  {selectedActivity.time}
                </p>
              )}
              
              {selectedActivity.duration && (
                <p className="text-blue-200 text-sm mb-3">
                  建議停留：{selectedActivity.duration}
                </p>
              )}
              
              {selectedActivity.description && (
                <p className="mb-4">{selectedActivity.description}</p>
              )}
              
              {selectedActivity.transport && (
                <div className="mb-2">
                  <h4 className="text-sm font-medium text-blue-300">交通資訊</h4>
                  <p className="text-sm">{selectedActivity.transport}</p>
                </div>
              )}
              
              {Array.isArray(selectedActivity.details) ? (
                <div className="mb-4">
                  <h4 className="text-sm font-medium text-blue-300 mb-1">詳細資訊</h4>
                  <ul className="list-disc list-inside space-y-1 text-sm">
                    {selectedActivity.details.map((detail, i) => (
                      <li key={i}>{detail}</li>
                    ))}
                  </ul>
                </div>
              ) : selectedActivity.details ? (
                <div className="mb-4">
                  <h4 className="text-sm font-medium text-blue-300 mb-1">詳細資訊</h4>
                  <p className="text-sm">{selectedActivity.details}</p>
                </div>
              ) : null}
              
              <button 
                onClick={closeActivityDetails}
                className="w-full py-3 bg-blue-600 hover:bg-blue-700 rounded-lg font-medium mt-3"
              >
                關閉
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default IcelandTripHub;
