// IcelandTripHub.js - ÂÑ™ÂåñÁâà
import React, { useState, useEffect } from 'react';
import { GoogleMap, LoadScript, Marker, InfoWindow } from '@react-google-maps/api';
import { Calendar, MapPin, Sun, Sunset, Clock, AlertCircle, Menu, X, CloudSnow, 
  Thermometer, Wind, Droplets, Compass, Camera, Info, Moon, FileText, 
  ChevronDown, ChevronRight, Image } from 'lucide-react';

const IcelandTripHub = () => {
  const [selectedDay, setSelectedDay] = useState(1);
  const [weather, setWeather] = useState(null);
  const [weatherForecast, setWeatherForecast] = useState([]);
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [selectedMarker, setSelectedMarker] = useState(null);
  const [expandedActivity, setExpandedActivity] = useState(null);
  const [darkMode, setDarkMode] = useState(true);
  const [activeSection, setActiveSection] = useState('itinerary');
  
  const mapKey = process.env.REACT_APP_GOOGLE_MAPS_API_KEY;

  // Iceland travel itinerary data
  const days = [
    {
      date: "3/2 (ÂÖ≠)",
      title: "ÊäµÈÅîÂÜ∞Â≥∂",
      sunrise: "08:32",
      sunset: "18:50",
      activities: [
        {
          type: "Ëà™Áè≠ÊäµÈÅî",
          details: "Âá±Â§´ÊãâÁ∂≠ÂÖãÂúãÈöõÊ©üÂ†¥ (Keflav√≠k International Airport, KEF)",
          coordinates: { lat: 63.985, lng: -22.605 },
          image: "/api/placeholder/500/300",
          description: "ÊäµÈÅîÂÜ∞Â≥∂ÁöÑÁ¨¨‰∏ÄÂ§©ÔºåÊàëÂÄëÂ∞áÂæûÂá±Â§´ÊãâÁ∂≠ÂÖãÂúãÈöõÊ©üÂ†¥ÈñãÂßãÊàëÂÄëÁöÑÂÜ∞Â≥∂ÂÜíÈö™„ÄÇ"
        },
        {
          type: "‰ΩèÂÆø",
          details: "Ê©üÂ†¥ÈôÑËøë‰ΩèÂÆø",
          image: "/api/placeholder/500/300",
          description: "Âú®Ê©üÂ†¥ÈôÑËøë‰ºëÊÅØÔºåÁÇ∫Êé•‰∏ã‰æÜÁöÑË°åÁ®ãÈ§äÁ≤æËìÑÈä≥„ÄÇ"
        }
      ]
    },
    // Ê≠§Ëôï‰øùÁïôÂéüÊúâÁöÑË°åÁ®ãÊï∏ÊìöÔºà‰øùÊåÅÊâÄÊúâË°åÁ®ãÊó•ÊúüËàáÊ¥ªÂãïÔºâ
    {
      date: "3/3 (Êó•)",
      title: "ÊñØÂ•àÂ±±ÂçäÂ≥∂",
      sunrise: "08:28",
      sunset: "18:53",
      activities: [
        {
          time: "‰∏äÂçà",
          location: "Gunnuhver ÁÅ´Â±±Âú∞ÁÜ±ÊôØÈªû",
          duration: "1-1.5 Â∞èÊôÇ",
          details: "ÂèÉËßÄÁÅ´Â±±Âú∞ÁÜ±ÂçÄÔºåÊ¨£Ë≥ûÊ≥•ÊºøÊ±†ÂíåÂô¥Ê∞£Â≠îÁ≠âÁâπÊÆäÂú∞Ë≤å",
          transport: "Ê©üÂ†¥ÈôÑËøë‰ΩèÂÆø ‚Üí GunnuhverÔºöüöó Á¥Ñ 10 ÂàÜÈêò",
          coordinates: { lat: 63.818, lng: -22.682 },
          image: "/api/placeholder/500/300",
          description: "GunnuhverÊòØÂÜ∞Â≥∂ÊúÄÂ§ßÁöÑÂú∞ÁÜ±ÂçÄ‰πã‰∏ÄÔºå‰ª•ÂÖ∂Ëí∏Ê±ΩÂô¥Âè£ÂíåÊ≥•ÊºøÊ±†ËÄåËÅûÂêç„ÄÇÈÄôË£°ÈÇÑÊúâÈóúÊñºÂ•≥Â∑´GunnaÁöÑÊ∞ëÈñìÂÇ≥Ë™™ÔºåÁÇ∫ÈÄôÂÄãÂú∞ÊñπÂ¢ûÊ∑ª‰∫ÜÁ•ûÁßòËâ≤ÂΩ©„ÄÇ"
        },
