// src/components/iceland-trip/liveInfo.js
import React, { useState, useEffect } from 'react';
import { Info, MapPin, AlertCircle, RefreshCw } from 'lucide-react';

const LiveInfo = ({ theme }) => {
  // Remove default values to clearly see when API data is loaded
  const [northernLightsData, setNorthernLightsData] = useState(null);
  const [roadConditions, setRoadConditions] = useState(null);
  const [localNews, setLocalNews] = useState(null);
  
  const [loading, setLoading] = useState({
    aurora: true,
    roads: true,
    news: true
  });
  
  const [error, setError] = useState({
    aurora: null,
    roads: null,
    news: null
  });

  const [lastRefreshed, setLastRefreshed] = useState(new Date());

  // Manual refresh function
  const handleRefresh = () => {
    setLastRefreshed(new Date());
    setLoading({
      aurora: true,
      roads: true,
      news: true
    });
    fetchAuroraData();
    fetchRoadConditions();
    fetchLocalNews();
  };

  // Northern Lights forecast - Using Vedur API
  const fetchAuroraData = async () => {
    try {
      setLoading(prev => ({ ...prev, aurora: true }));
      
      // Use the official Vedur.is API
      // Documentation indicates aurora forecast endpoint
      const response = await fetch('https://apis.is/weather/aurora');
      
      if (!response.ok) {
        throw new Error('Failed to fetch aurora data');
      }
      
      const data = await response.json();
      console.log('Aurora data:', data);
      
      // Format the data according to the API response
      if (data) {
        setNorthernLightsData({
          forecast: `${data.forecast || '?'}/9`,
          bestTime: "22:00 - 01:00",
          bestLocations: ["Grundarfjörður", "Jökulsárlón", "Reykjahlíð"],
          visibility: data.clouds || 'Unknown cloud coverage',
          lastUpdated: formatLastUpdated(new Date())
        });
      }
      
      setError(prev => ({ ...prev, aurora: null }));
    } catch (err) {
      console.error("Error fetching aurora data:", err);
      setError(prev => ({ ...prev, aurora: err.message }));
    } finally {
      setLoading(prev => ({ ...prev, aurora: false }));
    }
  };
  
  // Road conditions
  const fetchRoadConditions = async () => {
    try {
      setLoading(prev => ({ ...prev, roads: true }));
      
      // Using road.is API for road conditions
      const response = await fetch('https://www.road.is/api/faerdogtaefaerisupplysingar/v1/map');
      
      if (!response.ok) {
        throw new Error('Failed to fetch road conditions');
      }
      
      const data = await response.json();
      console.log('Road data:', data);
      
      if (data && data.data) {
        // Focus on main roads
        const mainRoadIds = ['1', '36', '35']; // Route 1, Route 36, Route 35
        
        const processedRoadData = mainRoadIds.map(roadId => {
          const roadData = data.data.find(r => String(r.road_number) === roadId);
          
          if (!roadData) {
            return null;
          }
          
          return {
            road: getRoadName(roadId),
            status: roadData.is_closed ? "Closed" : "Open",
            conditions: getRoadConditionFromCode(roadData.condition_code),
            lastUpdated: formatLastUpdated(new Date(roadData.last_updated * 1000))
          };
        }).filter(Boolean); // Remove null values
        
        // Add F-Roads as a group
        const fRoads = data.data.filter(r => String(r.road_number).startsWith('F'));
        if (fRoads.length > 0) {
          const fRoadsClosed = fRoads.every(r => r.is_closed);
          
          processedRoadData.push({
            road: "F-Roads (Highland Roads)",
            status: fRoadsClosed ? "Closed" : "Some Open",
            conditions: "Winter Closure",
            lastUpdated: formatLastUpdated(new Date())
          });
        }
        
        setRoadConditions(processedRoadData);
      }
      
      setError(prev => ({ ...prev, roads: null }));
    } catch (err) {
      console.error("Error fetching road conditions:", err);
      setError(prev => ({ ...prev, roads: err.message }));
    } finally {
      setLoading(prev => ({ ...prev, roads: false }));
    }
  };
  
  // Local news
  const fetchLocalNews = async () => {
    try {
      setLoading(prev => ({ ...prev, news: true }));
      
      // Using a news API proxy for Iceland news
      const response = await fetch('https://cors-anywhere.herokuapp.com/https://icelandmonitor.mbl.is/news/');
      
      if (!response.ok) {
        throw new Error('Failed to fetch news');
      }
      
      const htmlText = await response.text();
      console.log('News HTML:', htmlText.substring(0, 200)); // Log the first 200 chars
      
      // Simple parse of the HTML to extract news items
      const newsRegex = /<h2><a href="([^"]+)">([^<]+)<\/a><\/h2>.*?<time datetime="([^"]+)">/g;
      let match;
      const newsItems = [];
      
      while ((match = newsRegex.exec(htmlText)) !== null && newsItems.length < 3) {
        const url = match[1];
        const title = match[2];
        const pubDate = new Date(match[3]);
        
        newsItems.push({
          title: title,
          time: formatNewsTime(pubDate),
          url: url
        });
      }
      
      if (newsItems.length > 0) {
        setLocalNews(newsItems);
      }
      
      setError(prev => ({ ...prev, news: null }));
    } catch (err) {
      console.error("Error fetching news:", err);
      setError(prev => ({ ...prev, news: err.message }));
    } finally {
      setLoading(prev => ({ ...prev, news: false }));
    }
  };
  
  // Initialize data on component mount
  useEffect(() => {
    fetchAuroraData();
    fetchRoadConditions();
    fetchLocalNews();
    
    // Set up interval refreshes
    const auroraAndRoadInterval = setInterval(() => {
      fetchAuroraData();
      fetchRoadConditions();
    }, 60 * 60 * 1000);
    
    const newsInterval = setInterval(fetchLocalNews, 15 * 60 * 1000);
    
    return () => {
      clearInterval(auroraAndRoadInterval);
      clearInterval(newsInterval);
    };
  }, []);
  
  // Convert condition code to text description
  const getRoadConditionFromCode = (conditionCode) => {
    const conditionMap = {
      1: "Good",
      2: "Wet",
      3: "Icy",
      4: "Partly Icy",
      5: "Snow-covered",
      6: "Icy Snow",
      7: "Closed",
      8: "Winter Closure"
    };
    
    return conditionMap[conditionCode] || "Unknown";
  };
  
  // Format timestamp to relative time
  const formatLastUpdated = (dateObj) => {
    if (!dateObj) return "Unknown";
    
    const now = new Date();
    
    if (dateObj.toDateString() === now.toDateString()) {
      return `Today ${dateObj.getHours().toString().padStart(2, '0')}:${dateObj.getMinutes().toString().padStart(2, '0')}`;
    } else {
      return `Yesterday ${dateObj.getHours().toString().padStart(2, '0')}:${dateObj.getMinutes().toString().padStart(2, '0')}`;
    }
  };
  
  // Format news timestamp
  const formatNewsTime = (date) => {
    const now = new Date();
    const diffInMinutes = Math.floor((now - date) / (1000 * 60));
    
    if (diffInMinutes < 60) {
      return `${diffInMinutes} min ago`;
    } else if (diffInMinutes < 24 * 60) {
      return `${Math.floor(diffInMinutes / 60)} hours ago`;
    } else {
      return `${Math.floor(diffInMinutes / (60 * 24))} days ago`;
    }
  };
  
  // Get road name from ID
  const getRoadName = (roadId) => {
    const roadNames = {
      "1": "Route 1 (Ring Road)",
      "36": "Route 36 (Golden Circle)",
      "35": "Route 35 (Kjölur)"
    };
    
    return roadNames[roadId] || `Route ${roadId}`;
  };

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center mb-4">
        <h2 className={`text-xl font-semibold flex items-center gap-2`}>
          <Info className={`h-6 w-6 ${theme.highlight}`} />
          <span>Iceland Live Information</span>
        </h2>
        
        <button 
          onClick={handleRefresh}
          className={`flex items-center gap-1 px-3 py-1 rounded-full ${theme.button} text-white text-sm`}
          disabled={loading.aurora || loading.roads || loading.news}
        >
          <RefreshCw className={`h-4 w-4 ${(loading.aurora || loading.roads || loading.news) ? 'animate-spin' : ''}`} />
          <span>Refresh</span>
        </button>
      </div>
      
      <div className={`text-xs ${theme.secondaryText} mb-2 text-right`}>
        Last updated: {lastRefreshed.toLocaleTimeString()}
      </div>
      
      {/* Northern Lights Forecast */}
      <div className={`${theme.cardBg} rounded-xl p-5 ${theme.cardBorder}`}>
        <h3 className="text-lg font-semibold mb-4">Aurora Forecast</h3>
        
        {loading.aurora ? (
          <div className="text-center py-6">
            <div className="animate-pulse flex justify-center">
              <div className="h-8 w-8 bg-blue-400 rounded-full opacity-50"></div>
            </div>
            <p className="mt-2 text-sm">Loading aurora forecast...</p>
          </div>
        ) : error.aurora ? (
          <div className="text-center py-2 text-red-500">
            <p className="text-sm">Failed to get aurora forecast: {error.aurora}</p>
          </div>
        ) : northernLightsData ? (
          <div className={`grid grid-cols-1 md:grid-cols-3 gap-4 ${theme.secondaryText}`}>
            <div className={`${theme.darkMode ? 'bg-blue-800 bg-opacity-40' : 'bg-blue-50'} rounded-lg p-4`}>
              <div className="text-center">
                <div className="text-xl font-bold mb-1">{northernLightsData.forecast}</div>
                <div className="text-sm">Tonight's Activity</div>
              </div>
              <div className="mt-3 h-4 bg-gray-200 rounded-full overflow-hidden">
                <div 
                  className="h-full bg-gradient-to-r from-blue-400 via-purple-500 to-green-400" 
                  style={{width: `${parseInt(northernLightsData.forecast) / 9 * 100}%`}}
                ></div>
              </div>
            </div>
            
            <div>
              <div className="flex justify-between items-center border-b border-opacity-20 pb-2 mb-2">
                <span>Best Viewing Time</span>
                <span className="font-medium">{northernLightsData.bestTime}</span>
              </div>
              <div className="flex justify-between items-center border-b border-opacity-20 pb-2 mb-2">
                <span>Cloud Coverage</span>
                <span className="font-medium">{northernLightsData.visibility}</span>
              </div>
              <div className="flex justify-between items-center pb-2">
                <span>Last Updated</span>
                <span className="font-medium">{northernLightsData.lastUpdated}</span>
              </div>
            </div>
            
            <div>
              <div className="font-medium mb-2">Recommended Locations</div>
              {northernLightsData.bestLocations && northernLightsData.bestLocations.length > 0 ? (
                <ul className="space-y-1">
                  {northernLightsData.bestLocations.map((location, idx) => (
                    <li key={idx} className="flex items-center gap-1">
                      <MapPin className="w-4 h-4" />
                      <span>{location}</span>
                    </li>
                  ))}
                </ul>
              ) : (
                <p>No recommended locations available</p>
              )}
            </div>
          </div>
        ) : (
          <div className="text-center py-4">
            <p className="text-sm">No aurora data available</p>
          </div>
        )}
        
        <div className="text-xs text-right mt-3 opacity-70">
          <span>Data source: Vedur.is Aurora Forecast</span>
        </div>
      </div>
      
      {/* Road Conditions */}
      <div className={`${theme.cardBg} rounded-xl p-5 ${theme.cardBorder}`}>
        <h3 className="text-lg font-semibold mb-4">Road Conditions</h3>
        
        {loading.roads ? (
          <div className="text-center py-6">
            <div className="animate-pulse flex justify-center">
              <div className="h-6 w-32 bg-gray-300 rounded opacity-50"></div>
            </div>
            <p className="mt-2 text-sm">Loading road conditions...</p>
          </div>
        ) : error.roads ? (
          <div className="text-center py-2 text-red-500">
            <p className="text-sm">Failed to get road conditions: {error.roads}</p>
          </div>
        ) : roadConditions && roadConditions.length > 0 ? (
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead className={`${theme.darkMode ? 'bg-blue-900 bg-opacity-50' : 'bg-blue-50'} text-left`}>
                <tr>
                  <th className="p-3 rounded-tl-lg">Road Name</th>
                  <th className="p-3">Status</th>
                  <th className="p-3">Conditions</th>
                  <th className="p-3 rounded-tr-lg">Updated</th>
                </tr>
              </thead>
              <tbody className={`${theme.secondaryText}`}>
                {roadConditions.map((road, idx) => (
                  <tr key={idx} className={idx % 2 === 0 ? (theme.darkMode ? 'bg-black bg-opacity-10' : 'bg-gray-50') : ''}>
                    <td className="p-3 font-medium">{road.road}</td>
                    <td className="p-3">
                      <span className={`inline-flex items-center px-2 py-1 rounded-full text-xs ${
                        road.status === 'Open' 
                          ? (theme.darkMode ? 'bg-green-900 bg-opacity-50 text-green-200' : 'bg-green-100 text-green-800') 
                          : (theme.darkMode ? 'bg-red-900 bg-opacity-50 text-red-200' : 'bg-red-100 text-red-800')
                      }`}>
                        {road.status}
                      </span>
                    </td>
                    <td className="p-3">{road.conditions}</td>
                    <td className="p-3">{road.lastUpdated}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ) : (
          <div className="text-center py-4">
            <p className="text-sm">No road condition data available</p>
          </div>
        )}
        
        <div className="mt-4 text-center text-sm">
          <p>Data source: Icelandic Road Administration (Vegagerðin)</p>
          <a href="https://www.road.is/" target="_blank" rel="noopener noreferrer" className={`${theme.highlight} underline mt-1 inline-block`}>
            View detailed road info →
          </a>
        </div>
      </div>
      
      {/* Local News */}
      <div className={`${theme.cardBg} rounded-xl p-5 ${theme.cardBorder}`}>
        <h3 className="text-lg font-semibold mb-4">Iceland Latest News</h3>
        
        {loading.news ? (
          <div className="text-center py-6">
            <div className="animate-pulse space-y-2">
              {[1, 2, 3].map(i => (
                <div key={i} className="h-12 bg-gray-300 rounded opacity-30"></div>
              ))}
            </div>
            <p className="mt-2 text-sm">Loading news...</p>
          </div>
        ) : error.news ? (
          <div className="text-center py-2 text-red-500">
            <p className="text-sm">Failed to get news: {error.news}</p>
          </div>
        ) : localNews && localNews.length > 0 ? (
          <div className="space-y-3">
            {localNews.map((news, idx) => (
              <div key={idx} className={`p-3 ${theme.darkMode ? 'bg-blue-900 bg-opacity-30' : 'bg-blue-50'} rounded-lg`}>
                <h4 className="font-medium">{news.title}</h4>
                <div className={`text-xs ${theme.secondaryText} mt-1`}>{news.time}</div>
              </div>
            ))}
          </div>
        ) : (
          <div className="text-center py-4">
            <p className="text-sm">No news available</p>
          </div>
        )}
        
        <div className="text-xs text-right mt-3 opacity-70">
          <span>Data source: Iceland Monitor</span>
        </div>
      </div>
      
      {/* Safety Tips */}
      <div className={`${theme.cardBg} rounded-xl p-5 ${theme.cardBorder} border-amber-200 ${theme.darkMode ? 'border-opacity-30' : ''}`}>
        <div className="flex items-center gap-2 mb-3">
          <AlertCircle className={`w-5 h-5 ${theme.darkMode ? 'text-amber-400' : 'text-amber-500'}`} />
          <h3 className="text-lg font-semibold">Travel Safety Tips</h3>
        </div>
        
        <ul className="space-y-2">
          <li className="flex items-start gap-2">
            <span className={theme.darkMode ? 'text-amber-400' : 'text-amber-500'}>•</span>
            <span>Monitor Vedur.is and Road.is for weather alerts and road closures.</span>
          </li>
          <li className="flex items-start gap-2">
            <span className={theme.darkMode ? 'text-amber-400' : 'text-amber-500'}>•</span>
            <span>Emergency phone: 112. Download the 112 Iceland app.</span>
          </li>
          <li className="flex items-start gap-2">
            <span className={theme.darkMode ? 'text-amber-400' : 'text-amber-500'}>•</span>
            <span>Drive cautiously in winter. F-roads are closed in winter.</span>
          </li>
        </ul>
      </div>
    </div>
  );
};

export default LiveInfo;
