// src/components/iceland-trip/liveInfo.js
import React, { useState, useEffect } from 'react';
import { Info, CloudSnow, Wind, Droplets, Thermometer, AlertCircle, RefreshCw, ExternalLink } from 'lucide-react';

const LiveInfo = ({ theme }) => {
  const [weather, setWeather] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [lastRefreshed, setLastRefreshed] = useState(new Date());

  // Fetch weather data using OpenWeather API
  const fetchWeatherData = async () => {
    try {
      setLoading(true);
      
      const apiKey = process.env.REACT_APP_OPENWEATHER_API_KEY;
      if (!apiKey) {
        throw new Error('OpenWeather API key is missing');
      }
      
      const response = await fetch(
        `https://api.openweathermap.org/data/2.5/weather?q=Reykjavik,is&units=metric&appid=${apiKey}`
      );
      
      if (!response.ok) {
        throw new Error(`Weather API error: ${response.status}`);
      }
      
      const data = await response.json();
      console.log('Weather data:', data);
      
      setWeather({
        temperature: data.main.temp,
        condition: data.weather[0].description,
        icon: data.weather[0].icon,
        windSpeed: data.wind.speed,
        humidity: data.main.humidity,
        feelsLike: data.main.feels_like,
        city: data.name,
        country: data.sys.country,
        timestamp: new Date(data.dt * 1000)
      });
      
      setError(null);
    } catch (err) {
      console.error('Error fetching weather data:', err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  // Manual refresh function
  const handleRefresh = () => {
    setLastRefreshed(new Date());
    fetchWeatherData();
  };

  // Initialize data on component mount
  useEffect(() => {
    fetchWeatherData();
    
    // Set up interval refresh every 30 minutes
    const weatherInterval = setInterval(fetchWeatherData, 30 * 60 * 1000);
    
    return () => {
      clearInterval(weatherInterval);
    };
  }, []);

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center mb-4">
        <h2 className={`text-xl font-semibold flex items-center gap-2`}>
          <Info className={`h-6 w-6 ${theme.highlight}`} />
          <span>Iceland Live Information</span>
        </h2>
        
        <button 
          onClick={handleRefresh}
          className={`flex items-center gap-1 px-3 py-1 rounded-full ${theme.button} text-white text-sm`}
          disabled={loading}
        >
          <RefreshCw className={`h-4 w-4 ${loading ? 'animate-spin' : ''}`} />
          <span>Refresh</span>
        </button>
      </div>
      
      <div className={`text-xs ${theme.secondaryText} mb-2 text-right`}>
        Last updated: {lastRefreshed.toLocaleTimeString()}
      </div>
      
      {/* Current Weather */}
      <div className={`${theme.cardBg} rounded-xl p-5 ${theme.cardBorder}`}>
        <h3 className="text-lg font-semibold mb-4">Current Weather in Reykjavik</h3>
        
        {loading && !weather ? (
          <div className="text-center py-6">
            <div className="animate-pulse flex justify-center">
              <div className="h-8 w-8 bg-blue-400 rounded-full opacity-50"></div>
            </div>
            <p className="mt-2 text-sm">Loading weather data...</p>
          </div>
        ) : error ? (
          <div className="text-center py-2 text-red-500">
            <p className="text-sm">Failed to get weather data: {error}</p>
          </div>
        ) : weather ? (
          <div>
            <div className="flex items-center mb-4">
              {weather.icon && (
                <img 
                  src={`http://openweathermap.org/img/wn/${weather.icon}@2x.png`} 
                  alt={weather.condition}
                  className="w-16 h-16"
                />
              )}
              <div className="ml-2">
                <div className="text-3xl font-bold">{Math.round(weather.temperature)}°C</div>
                <div className="capitalize">{weather.condition}</div>
              </div>
            </div>
            
            <div className="grid grid-cols-2 md:grid-cols-3 gap-4 mt-4">
              <div className={`${theme.darkMode ? 'bg-blue-800 bg-opacity-30' : 'bg-blue-50'} p-3 rounded-lg flex items-center`}>
                <Wind className={`mr-2 h-5 w-5 ${theme.highlight}`} />
                <div>
                  <div className="text-sm text-opacity-70">Wind</div>
                  <div className="font-medium">{weather.windSpeed} m/s</div>
                </div>
              </div>
              
              <div className={`${theme.darkMode ? 'bg-blue-800 bg-opacity-30' : 'bg-blue-50'} p-3 rounded-lg flex items-center`}>
                <Droplets className={`mr-2 h-5 w-5 ${theme.highlight}`} />
                <div>
                  <div className="text-sm text-opacity-70">Humidity</div>
                  <div className="font-medium">{weather.humidity}%</div>
                </div>
              </div>
              
              <div className={`${theme.darkMode ? 'bg-blue-800 bg-opacity-30' : 'bg-blue-50'} p-3 rounded-lg flex items-center`}>
                <Thermometer className={`mr-2 h-5 w-5 ${theme.highlight}`} />
                <div>
                  <div className="text-sm text-opacity-70">Feels Like</div>
                  <div className="font-medium">{Math.round(weather.feelsLike)}°C</div>
                </div>
              </div>
            </div>
          </div>
        ) : (
          <div className="text-center py-4">
            <p className="text-sm">No weather data available</p>
          </div>
        )}
        
        <div className="text-xs text-right mt-3 opacity-70">
          <span>Data source: OpenWeather API</span>
        </div>
      </div>
      
      {/* Information Resources - Link Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        {/* Aurora Forecast Link Card */}
        <a 
          href="https://en.vedur.is/weather/forecasts/aurora/" 
          target="_blank" 
          rel="noopener noreferrer"
          className={`${theme.cardBg} rounded-xl p-5 ${theme.cardBorder} hover:shadow-lg transition-all transform hover:-translate-y-1`}
        >
          <div className="flex items-center gap-3 mb-2">
            <div className={`${theme.darkMode ? 'bg-purple-800' : 'bg-purple-100'} rounded-full p-3`}>
              <CloudSnow className={`h-6 w-6 ${theme.darkMode ? 'text-purple-200' : 'text-purple-600'}`} />
            </div>
            <h3 className="text-lg font-semibold">Aurora Forecast</h3>
          </div>
          
          <p className={`${theme.secondaryText} mb-3`}>
            Check the official aurora (northern lights) forecast from the Icelandic Met Office.
          </p>
          
          <div className="flex items-center text-sm font-medium mt-auto">
            <span className={theme.highlight}>View forecast</span>
            <ExternalLink className="h-4 w-4 ml-1" />
          </div>
        </a>
        
        {/* Road Conditions Link Card */}
        <a 
          href="https://www.road.is/travel-info/road-conditions-and-weather/" 
          target="_blank" 
          rel="noopener noreferrer"
          className={`${theme.cardBg} rounded-xl p-5 ${theme.cardBorder} hover:shadow-lg transition-all transform hover:-translate-y-1`}
        >
          <div className="flex items-center gap-3 mb-2">
            <div className={`${theme.darkMode ? 'bg-blue-800' : 'bg-blue-100'} rounded-full p-3`}>
              <Info className={`h-6 w-6 ${theme.darkMode ? 'text-blue-200' : 'text-blue-600'}`} />
            </div>
            <h3 className="text-lg font-semibold">Road Conditions</h3>
          </div>
          
          <p className={`${theme.secondaryText} mb-3`}>
            Get live updates on road conditions and closures from the Icelandic Road Administration.
          </p>
          
          <div className="flex items-center text-sm font-medium mt-auto">
            <span className={theme.highlight}>Check road status</span>
            <ExternalLink className="h-4 w-4 ml-1" />
          </div>
        </a>
      </div>
      
      {/* Safety Tips */}
      <div className={`${theme.cardBg} rounded-xl p-5 ${theme.cardBorder} border-amber-200 ${theme.darkMode ? 'border-opacity-30' : ''}`}>
        <div className="flex items-center gap-2 mb-3">
          <AlertCircle className={`w-5 h-5 ${theme.darkMode ? 'text-amber-400' : 'text-amber-500'}`} />
          <h3 className="text-lg font-semibold">Travel Safety Tips</h3>
        </div>
        
        <ul className="space-y-2">
          <li className="flex items-start gap-2">
            <span className={theme.darkMode ? 'text-amber-400' : 'text-amber-500'}>•</span>
            <span>Monitor Vedur.is and Road.is for weather alerts and road closures.</span>
          </li>
          <li className="flex items-start gap-2">
            <span className={theme.darkMode ? 'text-amber-400' : 'text-amber-500'}>•</span>
            <span>Emergency phone: 112. Download the 112 Iceland app for emergencies.</span>
          </li>
          <li className="flex items-start gap-2">
            <span className={theme.darkMode ? 'text-amber-400' : 'text-amber-500'}>•</span>
            <span>Drive cautiously in winter. F-roads are closed in winter months.</span>
          </li>
          <li className="flex items-start gap-2">
            <span className={theme.darkMode ? 'text-amber-400' : 'text-amber-500'}>•</span>
            <span>Always check SafeTravel.is before heading out for your day's journey.</span>
          </li>
        </ul>
        
        <div className="mt-4">
          <a 
            href="https://safetravel.is/" 
            target="_blank" 
            rel="noopener noreferrer"
            className={`inline-flex items-center ${theme.button} text-white px-4 py-2 rounded-lg text-sm font-medium`}
          >
            <span>Visit SafeTravel.is</span>
            <ExternalLink className="h-4 w-4 ml-2" />
          </a>
        </div>
      </div>
    </div>
  );
};

export default LiveInfo;
