// src/components/iceland-trip/liveInfo.js
import React, { useState, useEffect } from 'react';
import { Info, MapPin, AlertCircle } from 'lucide-react';

const LiveInfo = ({ theme }) => {
  const [northernLightsData, setNorthernLightsData] = useState({
    forecast: "4/9",
    bestTime: "22:00 - 01:00",
    bestLocations: ["Grundarfjörður", "Jökulsárlón", "Reykjahlíð"],
    visibility: "部分地區雲層覆蓋",
    lastUpdated: "今天 09:00"
  });
  
  const [roadConditions, setRoadConditions] = useState([
    { road: "Route 1 (環島公路)", status: "開放", conditions: "部分結冰", lastUpdated: "今天 08:30" },
    { road: "Route 36 (黃金圈)", status: "開放", conditions: "良好", lastUpdated: "今天 08:45" },
    { road: "F-Roads (高地道路)", status: "關閉", conditions: "冬季關閉", lastUpdated: "昨天 18:00" }
  ]);
  
  const [localNews, setLocalNews] = useState([
    { title: "冰島南部地區明日預計大雪", time: "1小時前" },
    { title: "雷克雅維克市區今日舉行冬季燈光節", time: "3小時前" },
    { title: "Strokkur間歇泉噴發頻率增加，遊客注意安全", time: "6小時前" }
  ]);
  
  const [loading, setLoading] = useState({
    aurora: true,
    roads: true,
    news: true
  });
  
  const [error, setError] = useState({
    aurora: null,
    roads: null,
    news: null
  });

  // 獲取北極光預報
  useEffect(() => {
    const fetchAuroraData = async () => {
      try {
        setLoading(prev => ({ ...prev, aurora: true }));
        
        // Vedur.is 北極光預報 API
        const apiKey = process.env.REACT_APP_VEDUR_API_KEY || '';
        const response = await fetch(
          `https://api.vedur.is/nor/forecasts/aurora/latest/${apiKey ? `?key=${apiKey}` : ''}`
        );
        
        if (!response.ok) {
          throw new Error('無法獲取北極光預報');
        }
        
        const data = await response.json();
        
        // 轉換數據
        // 注意：這裡的數據處理需要根據實際 API 返回的格式調整
        if (data) {
          // 假設 API 返回格式為 { aurora_activity: 4, forecast_time: "...", best_viewing_hours: {...}, cloud_coverage: "..." }
          setNorthernLightsData({
            forecast: `${data.aurora_activity || 4}/9`,
            bestTime: data.best_viewing_hours ? 
              `${data.best_viewing_hours.start || "22:00"} - ${data.best_viewing_hours.end || "01:00"}` : 
              "22:00 - 01:00",
            bestLocations: data.best_locations || ["Grundarfjörður", "Jökulsárlón", "Reykjahlíð"],
            visibility: data.cloud_coverage || "部分地區雲層覆蓋",
            lastUpdated: formatLastUpdated(data.forecast_time) || "今天 09:00"
          });
        }
        
        setError(prev => ({ ...prev, aurora: null }));
      } catch (err) {
        console.error("獲取北極光預報時出錯:", err);
        setError(prev => ({ ...prev, aurora: err.message }));
        // 保留默認數據，已在 useState 初始化中設置
      } finally {
        setLoading(prev => ({ ...prev, aurora: false }));
      }
    };
    
    // 獲取道路狀況
    const fetchRoadConditions = async () => {
      try {
        setLoading(prev => ({ ...prev, roads: true }));
        
        // 冰島道路管理局 API
        // 注意：這需要根據實際 API 調整
        const response = await fetch('https://www.road.is/api/umferdogvedur/vegalisti/1,36,35,F');
        
        if (!response.ok) {
          throw new Error('無法獲取道路狀況');
        }
        
        const data = await response.json();
        
        // 轉換數據
        if (data && data.results) {
          const processedRoadData = data.results.map(road => ({
            road: getRoadName(road.roadId), // 轉換道路 ID 為名稱的函數
            status: road.closed ? "關閉" : "開放",
            conditions: getRoadConditionDescription(road), // 獲取道路狀況描述的函數
            lastUpdated: formatLastUpdated(road.lastUpdated) // 格式化更新時間
          }));
          
          setRoadConditions(processedRoadData);
        }
        
        setError(prev => ({ ...prev, roads: null }));
      } catch (err) {
        console.error("獲取道路狀況時出錯:", err);
        setError(prev => ({ ...prev, roads: err.message }));
        // 保留默認數據，已在 useState 初始化中設置
      } finally {
        setLoading(prev => ({ ...prev, roads: false }));
      }
    };
    
    // 獲取當地新聞
    const fetchLocalNews = async () => {
      try {
        setLoading(prev => ({ ...prev, news: true }));
        
        // 冰島新聞 API 或 RSS 源
        // 這裡使用 mbl.is 的 RSS 源作為示例
        const response = await fetch('https://cors-anywhere.herokuapp.com/https://www.mbl.is/feeds/helst/');
        
        if (!response.ok) {
          throw new Error('無法獲取當地新聞');
        }
        
        const data = await response.text();
        
        // 解析 RSS 源
        // 這裡需要使用 DOM 解析器或專門的 RSS 解析庫
        // 簡化示例
        const parser = new DOMParser();
        const xml = parser.parseFromString(data, "application/xml");
        const items = xml.querySelectorAll("item");
        
        const news = Array.from(items).slice(0, 3).map(item => {
          const title = item.querySelector("title").textContent;
          const pubDate = new Date(item.querySelector("pubDate").textContent);
          
          return {
            title: title,
            time: formatNewsTime(pubDate)
          };
        });
        
        setLocalNews(news);
        setError(prev => ({ ...prev, news: null }));
      } catch (err) {
        console.error("獲取當地新聞時出錯:", err);
        setError(prev => ({ ...prev, news: err.message }));
        // 保留默認數據，已在 useState 初始化中設置
      } finally {
        setLoading(prev => ({ ...prev, news: false }));
      }
    };
    
    // 執行所有獲取數據的函數
    fetchAuroraData();
    fetchRoadConditions();
    fetchLocalNews();
    
    // 設置定時更新，每小時更新北極光和道路數據，每 15 分鐘更新新聞
    const auroraAndRoadInterval = setInterval(() => {
      fetchAuroraData();
      fetchRoadConditions();
    }, 60 * 60 * 1000);
    
    const newsInterval = setInterval(fetchLocalNews, 15 * 60 * 1000);
    
    return () => {
      clearInterval(auroraAndRoadInterval);
      clearInterval(newsInterval);
    };
  }, []);
  
  // 輔助函數
  const formatLastUpdated = (dateStr) => {
    if (!dateStr) return "未知";
    
    const date = new Date(dateStr);
    const now = new Date();
    
    if (date.toDateString() === now.toDateString()) {
      return `今天 ${date.getHours().toString().padStart(2, '0')}:${date.getMinutes().toString().padStart(2, '0')}`;
    } else {
      return `昨天 ${date.getHours().toString().padStart(2, '0')}:${date.getMinutes().toString().padStart(2, '0')}`;
    }
  };
  
  const formatNewsTime = (date) => {
    const now = new Date();
    const diffInMinutes = Math.floor((now - date) / (1000 * 60));
    
    if (diffInMinutes < 60) {
      return `${diffInMinutes}分鐘前`;
    } else if (diffInMinutes < 24 * 60) {
      return `${Math.floor(diffInMinutes / 60)}小時前`;
    } else {
      return `${Math.floor(diffInMinutes / (60 * 24))}天前`;
    }
  };
  
  const getRoadName = (roadId) => {
    // 根據道路 ID 獲取道路名稱的映射
    const roadNames = {
      "1": "Route 1 (環島公路)",
      "36": "Route 36 (黃金圈)",
      "35": "Route 35 (蘭德曼納勞卡)",
      "F": "F-Roads (高地道路)"
    };
    
    return roadNames[roadId] || `Route ${roadId}`;
  };
  
  const getRoadConditionDescription = (roadData) => {
    // 根據道路數據獲取狀況描述
    if (roadData.closed) return "冬季關閉";
    if (roadData.icy) return "部分結冰";
    if (roadData.snowCovered) return "積雪覆蓋";
    if (roadData.wet) return "路面濕滑";
    return "良好";
  };

  return (
    <div className="space-y-6">
      <h2 className={`text-xl font-semibold flex items-center gap-2 mb-4`}>
        <Info className={`h-6 w-6 ${theme.highlight}`} />
        <span>冰島即時資訊</span>
      </h2>
      
      {/* 北極光預報 */}
      <div className={`${theme.cardBg} rounded-xl p-5 ${theme.cardBorder}`}>
        <h3 className="text-lg font-semibold mb-4">北極光預報</h3>
        
        {loading.aurora ? (
          <div className="text-center py-6">
            <div className="animate-pulse flex justify-center">
              <div className="h-8 w-8 bg-blue-400 rounded-full opacity-50"></div>
            </div>
            <p className="mt-2 text-sm">載入北極光預報中...</p>
          </div>
        ) : error.aurora ? (
          <div className="text-center py-2 text-red-500">
            <p className="text-sm">無法獲取北極光預報。{error.aurora}</p>
          </div>
        ) : (
          <div className={`grid grid-cols-1 md:grid-cols-3 gap-4 ${theme.secondaryText}`}>
            <div className={`${theme.darkMode ? 'bg-blue-800 bg-opacity-40' : 'bg-blue-50'} rounded-lg p-4`}>
              <div className="text-center">
                <div className="text-xl font-bold mb-1">{northernLightsData.forecast}</div>
                <div className="text-sm">今晚活動指數</div>
              </div>
              <div className="mt-3 h-4 bg-gray-200 rounded-full overflow-hidden">
                <div 
                  className="h-full bg-gradient-to-r from-blue-400 via-purple-500 to-green-400" 
                  style={{width: `${parseInt(northernLightsData.forecast) / 9 * 100}%`}}
                ></div>
              </div>
            </div>
            
            <div>
              <div className="flex justify-between items-center border-b border-opacity-20 pb-2 mb-2">
                <span>最佳觀賞時間</span>
                <span className="font-medium">{northernLightsData.bestTime}</span>
              </div>
              <div className="flex justify-between items-center border-b border-opacity-20 pb-2 mb-2">
                <span>雲層狀況</span>
                <span className="font-medium">{northernLightsData.visibility}</span>
              </div>
              <div className="flex justify-between items-center pb-2">
                <span>最後更新</span>
                <span className="font-medium">{northernLightsData.lastUpdated}</span>
              </div>
            </div>
            
            <div>
              <div className="font-medium mb-2">推薦觀賞地點</div>
              <ul className="space-y-1">
                {northernLightsData.bestLocations.map((location, idx) => (
                  <li key={idx} className="flex items-center gap-1">
                    <MapPin className="w-4 h-4" />
                    <span>{location}</span>
                  </li>
                ))}
              </ul>
            </div>
          </div>
        )}
        
        <div className="text-xs text-right mt-3 opacity-70">
          <span>資料來源: Vedur.is 北極光預報</span>
        </div>
      </div>
      
      {/* 道路狀況 */}
      <div className={`${theme.cardBg} rounded-xl p-5 ${theme.cardBorder}`}>
        <h3 className="text-lg font-semibold mb-4">道路狀況</h3>
        
        {loading.roads ? (
          <div className="text-center py-6">
            <div className="animate-pulse flex justify-center">
              <div className="h-6 w-32 bg-gray-300 rounded opacity-50"></div>
            </div>
            <p className="mt-2 text-sm">載入道路狀況中...</p>
          </div>
        ) : error.roads ? (
          <div className="text-center py-2 text-red-500">
            <p className="text-sm">無法獲取道路狀況。{error.roads}</p>
          </div>
        ) : (
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead className={`${theme.darkMode ? 'bg-blue-900 bg-opacity-50' : 'bg-blue-50'} text-left`}>
                <tr>
                  <th className="p-3 rounded-tl-lg">道路名稱</th>
                  <th className="p-3">狀態</th>
                  <th className="p-3">路況</th>
                  <th className="p-3 rounded-tr-lg">更新時間</th>
                </tr>
              </thead>
              <tbody className={`${theme.secondaryText}`}>
                {roadConditions.map((road, idx) => (
                  <tr key={idx} className={idx % 2 === 0 ? (theme.darkMode ? 'bg-black bg-opacity-10' : 'bg-gray-50') : ''}>
                    <td className="p-3 font-medium">{road.road}</td>
                    <td className="p-3">
                      <span className={`inline-flex items-center px-2 py-1 rounded-full text-xs ${
                        road.status === '開放' 
                          ? (theme.darkMode ? 'bg-green-900 bg-opacity-50 text-green-200' : 'bg-green-100 text-green-800') 
                          : (theme.darkMode ? 'bg-red-900 bg-opacity-50 text-red-200' : 'bg-red-100 text-red-800')
                      }`}>
                        {road.status}
                      </span>
                    </td>
                    <td className="p-3">{road.conditions}</td>
                    <td className="p-3">{road.lastUpdated}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
        
        <div className="mt-4 text-center text-sm">
          <p>資料來源: Icelandic Road and Coastal Administration (Vegagerðin)</p>
          <a href="https://www.road.is/" target="_blank" rel="noopener noreferrer" className={`${theme.highlight} underline mt-1 inline-block`}>
            查看詳細路況資訊 →
          </a>
        </div>
      </div>
      
      {/* 當地新聞 */}
      <div className={`${theme.cardBg} rounded-xl p-5 ${theme.cardBorder}`}>
        <h3 className="text-lg font-semibold mb-4">冰島最新消息</h3>
        
        {loading.news ? (
          <div className="text-center py-6">
            <div className="animate-pulse space-y-2">
              {[1, 2, 3].map(i => (
                <div key={i} className="h-12 bg-gray-300 rounded opacity-30"></div>
              ))}
            </div>
            <p className="mt-2 text-sm">載入最新消息中...</p>
          </div>
        ) : error.news ? (
          <div className="text-center py-2 text-red-500">
            <p className="text-sm">無法獲取最新消息。{error.news}</p>
          </div>
        ) : (
          <div className="space-y-3">
            {localNews.map((news, idx) => (
              <div key={idx} className={`p-3 ${theme.darkMode ? 'bg-blue-900 bg-opacity-30' : 'bg-blue-50'} rounded-lg`}>
                <h4 className="font-medium">{news.title}</h4>
                <div className={`text-xs ${theme.secondaryText} mt-1`}>{news.time}</div>
              </div>
            ))}
          </div>
        )}
        
        <div className="text-xs text-right mt-3 opacity-70">
          <span>資料來源: mbl.is 冰島新聞</span>
        </div>
      </div>
    </div>
  );
};

export default LiveInfo;
