// src/components/iceland-trip/PenguinPet.js
import React, { useState, useEffect } from 'react';

const PenguinPet = ({ theme }) => {
  const [position, setPosition] = useState({ left: 20, top: window.innerHeight - 100 });
  const [isWaving, setIsWaving] = useState(false);
  const [isJumping, setIsJumping] = useState(false);
  const [message, setMessage] = useState('');
  const [isVisible, setIsVisible] = useState(true);
  const [direction, setDirection] = useState(1); // 1 for right, -1 for left

  // Messages the penguin can display
  const messages = [
    '冰島好冷啊！不過我喜歡～',
    '記得多拍照！',
    '黑沙灘超棒的！',
    '冰河湖是我的家～',
    '記得帶足夠的衣物！',
    '極光好美！',
    '小心路上結冰喔！',
    '今天行程如何？',
    '需要幫忙嗎？',
    '企鵝陪你看行程～',
    '點我可以跟我聊天唷！',
    '我是你的冰島旅遊小助手～'
  ];

  // Initialize screen bounds
  const [bounds, setBounds] = useState({
    width: typeof window !== 'undefined' ? window.innerWidth : 1000,
    height: typeof window !== 'undefined' ? window.innerHeight : 800
  });

  // Update screen bounds on resize
  useEffect(() => {
    const handleResize = () => {
      setBounds({
        width: window.innerWidth,
        height: window.innerHeight
      });
    };
    
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  useEffect(() => {
    // Move the penguin around the screen
    const moveInterval = setInterval(() => {
      if (Math.random() > 0.5) { // 50% chance to move
        // Decide movement distance and direction
        const moveX = (Math.random() * 100 + 50) * direction;
        const moveY = (Math.random() - 0.5) * 100;
        
        // Calculate new position
        const newLeft = Math.max(10, Math.min(bounds.width - 80, position.left + moveX));
        const newTop = Math.max(10, Math.min(bounds.height - 100, position.top + moveY));
        
        // Change direction if hitting screen edge
        if (newLeft <= 10 || newLeft >= bounds.width - 80) {
          setDirection(direction * -1);
        }
        
        setPosition({ left: newLeft, top: newTop });
        
        // Occasionally make the penguin jump while moving
        if (Math.random() > 0.7) {
          setIsJumping(true);
          setTimeout(() => setIsJumping(false), 500);
        }
      }
    }, 3000);

    return () => clearInterval(moveInterval);
  }, [position, direction, bounds]);

  const handleClick = () => {
    // Show a random message
    setMessage(messages[Math.floor(Math.random() * messages.length)]);
    
    // Always wave when clicked
    setIsWaving(true);
    setTimeout(() => setIsWaving(false), 800);
    
    // Jump with 70% probability
    if (Math.random() > 0.3) {
      setIsJumping(true);
      setTimeout(() => setIsJumping(false), 800);
    }
    
    // Change direction occasionally when clicked
    if (Math.random() > 0.7) {
      setDirection(direction * -1);
    }
    
    // Occasionally move to a new position when clicked
    if (Math.random() > 0.5) {
      const newLeft = Math.max(10, Math.min(bounds.width - 80, position.left + (Math.random() - 0.5) * 200));
      const newTop = Math.max(10, Math.min(bounds.height - 100, position.top + (Math.random() - 0.5) * 200));
      setPosition({ left: newLeft, top: newTop });
    }
    
    // Clear message after 4 seconds
    setTimeout(() => setMessage(''), 4000);
  };

  return (
    <>
      {isVisible && (
        <div 
          className="fixed z-50 transition-all duration-700 ease-in-out"
          style={{ 
            left: `${position.left}px`, 
            top: `${position.top}px`,
            transform: isJumping ? 'translateY(-20px)' : 'translateY(0)',
            cursor: 'pointer',
            transformOrigin: 'bottom center',
            transform: `scaleX(${direction}) ${isJumping ? 'translateY(-20px)' : 'translateY(0)'}`
          }}
        >
          {/* Message bubble */}
          {message && (
            <div className={`absolute bottom-full mb-2 p-3 rounded-lg max-w-xs text-sm ${theme.darkMode ? 'bg-blue-900 text-white' : 'bg-white text-blue-800'} border ${theme.darkMode ? 'border-blue-700' : 'border-blue-200'} shadow-lg`}>
              {message}
              <div className={`absolute bottom-[-8px] left-1/2 transform -translate-x-1/2 w-4 h-4 rotate-45 ${theme.darkMode ? 'bg-blue-900' : 'bg-white'} border-r ${theme.darkMode ? 'border-blue-700' : 'border-blue-200'} border-b ${theme.darkMode ? 'border-blue-700' : 'border-blue-200'}`}></div>
            </div>
          )}
          
          {/* Cuter Penguin SVG */}
          <div 
            onClick={handleClick}
            className={`${isWaving ? 'animate-wave' : ''} ${isJumping ? 'animate-jump' : ''}`}
          >
            <svg 
              width="80" 
              height="90" 
              viewBox="0 0 100 120" 
              fill="none" 
              xmlns="http://www.w3.org/2000/svg"
            >
              {/* Body - rounder, cuter shape */}
              <ellipse cx="50" cy="70" rx="35" ry="40" fill="#111827" />
              
              {/* White belly - bigger */}
              <ellipse cx="50" cy="75" rx="25" ry="30" fill="white" />
              
              {/* Head - rounder */}
              <circle cx="50" cy="30" r="20" fill="#111827" />
              
              {/* Cheeks - add rosy cheeks for cuteness */}
              <circle cx="35" cy="35" r="5" fill="#FFC0CB" opacity="0.3" />
              <circle cx="65" cy="35" r="5" fill="#FFC0CB" opacity="0.3" />
              
              {/* Face - bigger eyes */}
              <circle cx="40" cy="25" r="5" fill="white" />
              <circle cx="60" cy="25" r="5" fill="white" />
              
              {/* Pupils - with shine spot for cuteness */}
              <circle cx="42" cy="24" r="2.5" fill="black" />
              <circle cx="58" cy="24" r="2.5" fill="black" />
              <circle cx="43" cy="23" r="1" fill="white" />
              <circle cx="59" cy="23" r="1" fill="white" />
              
              {/* Eyebrows - for more expression */}
              <path d="M35 19 Q 40 17 45 19" stroke="#111827" strokeWidth="1.5" strokeLinecap="round" />
              <path d="M55 19 Q 60 17 65 19" stroke="#111827" strokeWidth="1.5" strokeLinecap="round" />
              
              {/* Beak - cuter, smaller */}
              <path d="M47 32 L53 32 L50 38 Z" fill="#FF9900" />
              
              {/* Wings - rounder shape */}
              <path d="M15 70 Q 30 50 30 90 Q 20 95 15 70" fill="#111827" />
              <path d="M85 70 Q 70 50 70 90 Q 80 95 85 70" fill="#111827" />
              
              {/* Feet - cuter */}
              <path d="M40 110 L45 100 L50 110 Z" fill="#FF9900" />
              <path d="M60 110 L55 100 L50 110 Z" fill="#FF9900" />
            </svg>
          </div>
          
          {/* Close button */}
          <button 
            onClick={(e) => {
              e.stopPropagation();
              setIsVisible(false);
              setTimeout(() => setIsVisible(true), 5000); // Reappear after 5 seconds
            }}
            className={`absolute top-0 right-0 w-6 h-6 rounded-full flex items-center justify-center text-xs ${theme.darkMode ? 'bg-blue-800 text-white hover:bg-blue-700' : 'bg-blue-100 text-blue-800 hover:bg-blue-200'} opacity-80 hover:opacity-100 transition-all duration-300 shadow-sm`}
          >
            ×
          </button>
        </div>
      )}
      
      {/* CSS Animations */}
      <style jsx>{`
        @keyframes wave {
          0% { transform: scaleX(${direction}) rotate(0deg); }
          25% { transform: scaleX(${direction}) rotate(-10deg); }
          50% { transform: scaleX(${direction}) rotate(0deg); }
          75% { transform: scaleX(${direction}) rotate(10deg); }
          100% { transform: scaleX(${direction}) rotate(0deg); }
        }
        
        @keyframes jump {
          0% { transform: scaleX(${direction}) translateY(0); }
          40% { transform: scaleX(${direction}) translateY(-25px); }
          50% { transform: scaleX(${direction}) translateY(-30px); }
          60% { transform: scaleX(${direction}) translateY(-25px); }
          100% { transform: scaleX(${direction}) translateY(0); }
        }
        
        .animate-wave {
          animation: wave 0.8s ease-in-out;
          transform-origin: center bottom;
        }
        
        .animate-jump {
          animation: jump 0.8s ease-in-out;
        }
      `}</style>
    </>
  );
};

export default PenguinPet;
