// src/components/iceland-trip/PenguinPet.js
import React, { useState, useEffect } from 'react';

const PenguinPet = ({ theme }) => {
  const [position, setPosition] = useState({ left: 20, bottom: 20 });
  const [isWaving, setIsWaving] = useState(false);
  const [isJumping, setIsJumping] = useState(false);
  const [message, setMessage] = useState('');
  const [isVisible, setIsVisible] = useState(true);

  // Messages the penguin can display
  const messages = [
    '冰島好冷啊！不過我喜歡～',
    '記得多拍照！',
    '黑沙灘超棒的！',
    '冰河湖是我的家～',
    '記得帶足夠的衣物！',
    '極光好美！',
    '小心路上結冰喔！',
    '今天行程如何？',
    '需要幫忙嗎？',
    '企鵝陪你看行程～'
  ];

  useEffect(() => {
    // Occasionally move the penguin
    const moveInterval = setInterval(() => {
      if (Math.random() > 0.7) { // 30% chance to move
        const newLeft = Math.max(10, Math.min(window.innerWidth - 100, position.left + (Math.random() - 0.5) * 100));
        const newBottom = Math.max(10, Math.min(200, position.bottom + (Math.random() - 0.5) * 50));
        setPosition({ left: newLeft, bottom: newBottom });
      }
    }, 3000);

    return () => clearInterval(moveInterval);
  }, [position]);

  const handleClick = () => {
    // Show a random message
    setMessage(messages[Math.floor(Math.random() * messages.length)]);
    // Wave animation
    setIsWaving(true);
    setTimeout(() => setIsWaving(false), 1000);
    
    // Jump sometimes
    if (Math.random() > 0.5) {
      setIsJumping(true);
      setTimeout(() => setIsJumping(false), 500);
    }
    
    // Clear message after 3 seconds
    setTimeout(() => setMessage(''), 3000);
  };

  return (
    <>
      {isVisible && (
        <div 
          className="fixed z-50 transition-all duration-300 ease-in-out"
          style={{ 
            left: `${position.left}px`, 
            bottom: `${position.bottom}px`,
            transform: isJumping ? 'translateY(-20px)' : 'translateY(0)',
            cursor: 'pointer'
          }}
        >
          {/* Message bubble */}
          {message && (
            <div className={`absolute bottom-full mb-2 p-2 rounded-lg max-w-xs text-sm ${theme.darkMode ? 'bg-blue-900 text-white' : 'bg-white text-blue-800'} border ${theme.darkMode ? 'border-blue-700' : 'border-blue-200'} shadow-lg`}>
              {message}
              <div className={`absolute bottom-[-8px] left-1/2 transform -translate-x-1/2 w-4 h-4 rotate-45 ${theme.darkMode ? 'bg-blue-900' : 'bg-white'} border-r ${theme.darkMode ? 'border-blue-700' : 'border-blue-200'} border-b ${theme.darkMode ? 'border-blue-700' : 'border-blue-200'}`}></div>
            </div>
          )}
          
          {/* Penguin SVG */}
          <div 
            onClick={handleClick}
            className={`${isWaving ? 'animate-wave' : ''} ${isJumping ? 'animate-jump' : ''}`}
          >
            <svg 
              width="70" 
              height="80" 
              viewBox="0 0 70 80" 
              fill="none" 
              xmlns="http://www.w3.org/2000/svg"
            >
              {/* Body */}
              <ellipse cx="35" cy="45" rx="25" ry="35" fill="black" />
              
              {/* White belly */}
              <ellipse cx="35" cy="50" rx="15" ry="25" fill="white" />
              
              {/* Head */}
              <circle cx="35" cy="20" r="15" fill="black" />
              
              {/* Face */}
              <circle cx="30" cy="16" r="2" fill="white" />
              <circle cx="40" cy="16" r="2" fill="white" />
              <circle cx="30" cy="16" r="1" fill="black" />
              <circle cx="40" cy="16" r="1" fill="black" />
              
              {/* Beak */}
              <path d="M32 22 L38 22 L35 26 Z" fill="orange" />
              
              {/* Wings */}
              <path d="M10 45 Q 20 30 20 55 Q 15 60 10 45" fill="black" />
              <path d="M60 45 Q 50 30 50 55 Q 55 60 60 45" fill="black" />
              
              {/* Feet */}
              <path d="M25 77 L30 70 L35 77 Z" fill="orange" />
              <path d="M45 77 L40 70 L35 77 Z" fill="orange" />
            </svg>
          </div>
          
          {/* Close button */}
          <button 
            onClick={(e) => {
              e.stopPropagation();
              setIsVisible(false);
              setTimeout(() => setIsVisible(true), 5000); // Reappear after 5 seconds
            }}
            className={`absolute top-0 right-0 w-5 h-5 rounded-full flex items-center justify-center text-xs ${theme.darkMode ? 'bg-gray-800 text-white' : 'bg-gray-200 text-gray-800'} opacity-70 hover:opacity-100`}
          >
            ×
          </button>
        </div>
      )}
      
      {/* CSS Animations */}
      <style jsx>{`
        @keyframes wave {
          0% { transform: rotate(0deg); }
          25% { transform: rotate(-5deg); }
          50% { transform: rotate(0deg); }
          75% { transform: rotate(5deg); }
          100% { transform: rotate(0deg); }
        }
        
        @keyframes jump {
          0% { transform: translateY(0); }
          50% { transform: translateY(-15px); }
          100% { transform: translateY(0); }
        }
        
        .animate-wave {
          animation: wave 0.5s ease-in-out;
        }
        
        .animate-jump {
          animation: jump 0.5s ease-in-out;
        }
      `}</style>
    </>
  );
};

export default PenguinPet;
