// src/components/iceland-trip/weatherWidget.js
import React, { useState, useEffect } from 'react';
import { CloudSnow, Wind, Droplets, Thermometer, Sun, Sunset, RefreshCw, Map, ChevronLeft, ChevronRight } from 'lucide-react';

const WeatherWidget = ({ theme }) => {
  const [weather, setWeather] = useState(null);
  const [forecast, setForecast] = useState(null);
  const [location, setLocation] = useState({
    lat: 64.1466,  // Default to Reykjavik
    lon: -21.9426,
    name: "Reykjavik",
    country: "IS"
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [lastRefreshed, setLastRefreshed] = useState(new Date());
  const [usingGeolocation, setUsingGeolocation] = useState(false);
  const [showForecast, setShowForecast] = useState(false);
  const [activeForecastDay, setActiveForecastDay] = useState(0);

  // Location-based weather
  const getUserLocation = () => {
    setLoading(true);
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setUsingGeolocation(true);
          fetchWeatherByCoords(position.coords.latitude, position.coords.longitude);
        },
        (error) => {
          console.error("Error getting location:", error);
          // Fall back to Reykjavik
          fetchWeatherByCity("Reykjavik,is");
          setError("Couldn't get your location. Showing Reykjavik weather instead.");
        }
      );
    } else {
      fetchWeatherByCity("Reykjavik,is");
      setError("Geolocation is not supported by your browser. Showing Reykjavik weather instead.");
    }
  };

  // Fetch weather by coordinates
  const fetchWeatherByCoords = async (lat, lon) => {
    try {
      setLoading(true);
      
      const apiKey = process.env.REACT_APP_OPENWEATHER_API_KEY;
      if (!apiKey) {
        throw new Error('OpenWeather API key is missing');
      }
      
      // Use OpenWeather's OneCall API for complete data
      const response = await fetch(
        `https://api.openweathermap.org/data/3.0/onecall?lat=${lat}&lon=${lon}&units=metric&exclude=minutely,alerts&appid=${apiKey}`
      );
      
      if (!response.ok) {
        throw new Error(`Weather API error: ${response.status}`);
      }
      
      const data = await response.json();
      console.log('Complete weather data:', data);
      
      // Get location name using reverse geocoding
      const geoResponse = await fetch(
        `https://api.openweathermap.org/geo/1.0/reverse?lat=${lat}&lon=${lon}&limit=1&appid=${apiKey}`
      );
      
      if (!geoResponse.ok) {
        throw new Error(`Geocoding API error: ${geoResponse.status}`);
      }
      
      const geoData = await geoResponse.json();
      console.log('Location data:', geoData);
      
      if (geoData && geoData.length > 0) {
        setLocation({
          lat,
          lon,
          name: geoData[0].name,
          country: geoData[0].country
        });
      } else {
        setLocation({
          lat,
          lon,
          name: "Unknown Location",
          country: ""
        });
      }
      
      // Process current weather
      setWeather({
        temperature: data.current.temp,
        condition: data.current.weather[0].description,
        icon: data.current.weather[0].icon,
        windSpeed: data.current.wind_speed,
        humidity: data.current.humidity,
        feelsLike: data.current.feels_like,
        uvi: data.current.uvi,
        visibility: data.current.visibility,
        sunrise: new Date(data.current.sunrise * 1000),
        sunset: new Date(data.current.sunset * 1000),
        timestamp: new Date(data.current.dt * 1000)
      });
      
      // Process forecast
      setForecast(data.daily.map(day => ({
        date: new Date(day.dt * 1000),
        tempMin: day.temp.min,
        tempMax: day.temp.max,
        condition: day.weather[0].description,
        icon: day.weather[0].icon,
        windSpeed: day.wind_speed,
        humidity: day.humidity,
        pop: day.pop, // Probability of precipitation
        sunrise: new Date(day.sunrise * 1000),
        sunset: new Date(day.sunset * 1000)
      })));
      
      setError(null);
    } catch (err) {
      console.error('Error fetching weather data:', err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  // Fetch weather by city name
  const fetchWeatherByCity = async (cityName) => {
    try {
      setLoading(true);
      
      const apiKey = process.env.REACT_APP_OPENWEATHER_API_KEY;
      if (!apiKey) {
        throw new Error('OpenWeather API key is missing');
      }
      
      // First get coordinates
      const geoResponse = await fetch(
        `https://api.openweathermap.org/geo/1.0/direct?q=${cityName}&limit=1&appid=${apiKey}`
      );
      
      if (!geoResponse.ok) {
        throw new Error(`Geocoding API error: ${geoResponse.status}`);
      }
      
      const geoData = await geoResponse.json();
      console.log('Location data:', geoData);
      
      if (geoData && geoData.length > 0) {
        const { lat, lon, name, country } = geoData[0];
        setLocation({ lat, lon, name, country });
        fetchWeatherByCoords(lat, lon);
      } else {
        throw new Error(`Location not found: ${cityName}`);
      }
    } catch (err) {
      console.error('Error fetching city data:', err);
      setError(err.message);
      setLoading(false);
    }
  };

  // Manual refresh function
  const handleRefresh = () => {
    setLastRefreshed(new Date());
    if (usingGeolocation) {
      getUserLocation();
    } else {
      fetchWeatherByCoords(location.lat, location.lon);
    }
  };

  // Format time function
  const formatTime = (date) => {
    return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  // Format date function
  const formatDate = (date) => {
    return date.toLocaleDateString([], { weekday: 'short', month: 'short', day: 'numeric' });
  };

  // Initialize data on component mount
  useEffect(() => {
    getUserLocation();
    
    // Set up interval refresh every 30 minutes
    const weatherInterval = setInterval(handleRefresh, 30 * 60 * 1000);
    
    return () => {
      clearInterval(weatherInterval);
    };
  }, []);

  // Next/Previous forecast day
  const nextForecastDay = () => {
    setActiveForecastDay(prev => Math.min(prev + 1, forecast ? forecast.length - 1 : 0));
  };

  const prevForecastDay = () => {
    setActiveForecastDay(prev => Math.max(prev - 1, 0));
  };

  return (
    <div className={`mb-6 ${theme.cardBg} rounded-xl p-4 ${theme.cardBorder}`}>
      <div className="flex justify-between items-center mb-3">
        <div className="flex items-center gap-2">
          <CloudSnow className={`h-5 w-5 ${theme.highlight}`} />
          <h2 className="text-lg font-semibold">天氣預報</h2>
        </div>
        
        <div className="flex items-center gap-2">
          <button 
            onClick={() => setShowForecast(!showForecast)}
            className={`text-xs px-2 py-1 rounded ${theme.darkMode ? 'bg-blue-800 hover:bg-blue-700' : 'bg-blue-100 hover:bg-blue-200'}`}
          >
            {showForecast ? '當前天氣' : '7天預報'}
          </button>
          
          <button 
            onClick={handleRefresh}
            className={`flex items-center gap-1 p-1 rounded-full ${theme.button} text-white text-sm`}
            disabled={loading}
            title="刷新天氣資料"
          >
            <RefreshCw className={`h-4 w-4 ${loading ? 'animate-spin' : ''}`} />
          </button>
        </div>
      </div>
      
      {/* Location info */}
      <div className="flex justify-between items-center mb-2">
        <div className="flex items-center gap-1 text-sm">
          <Map className="h-4 w-4" />
          <span>{location.name}, {location.country}</span>
          {usingGeolocation && <span className="text-xs opacity-70">(定位位置)</span>}
        </div>
        <div className={`text-xs ${theme.secondaryText}`}>
          最後更新: {lastRefreshed.toLocaleTimeString()}
        </div>
      </div>
      
      {/* Loading state */}
      {loading && !weather ? (
        <div className="text-center py-4">
          <div className="animate-pulse flex justify-center">
            <div className="h-8 w-8 bg-blue-400 rounded-full opacity-50"></div>
          </div>
          <p className="mt-2 text-sm">載入天氣資料中...</p>
        </div>
      ) : error && !weather ? (
        <div className="text-center py-2 text-red-500">
          <p className="text-sm">{error}</p>
        </div>
      ) : (
        <>
          {/* Current weather or forecast */}
          {!showForecast ? (
            /* Current weather display */
            weather && (
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div className="flex items-center">
                  {weather.icon && (
                    <img 
                      src={`http://openweathermap.org/img/wn/${weather.icon}@2x.png`} 
                      alt={weather.condition}
                      className="w-16 h-16"
                    />
                  )}
                  <div>
                    <div className="text-3xl font-bold">{Math.round(weather.temperature)}°C</div>
                    <div className="capitalize">{weather.condition}</div>
                  </div>
                </div>
                
                <div className="grid grid-cols-2 gap-2">
                  <div className="flex items-center">
                    <Wind className={`mr-2 h-4 w-4 ${theme.highlight}`} />
                    <div className="text-sm">風速: {weather.windSpeed} m/s</div>
                  </div>
                  
                  <div className="flex items-center">
                    <Droplets className={`mr-2 h-4 w-4 ${theme.highlight}`} />
                    <div className="text-sm">濕度: {weather.humidity}%</div>
                  </div>
                  
                  <div className="flex items-center">
                    <Thermometer className={`mr-2 h-4 w-4 ${theme.highlight}`} />
                    <div className="text-sm">體感: {Math.round(weather.feelsLike)}°C</div>
                  </div>
                  
                  <div className="flex items-center">
                    <Sun className={`mr-2 h-4 w-4 ${theme.highlight}`} />
                    <div className="text-sm">UV指數: {Math.round(weather.uvi)}</div>
                  </div>
                </div>
                
                <div className="col-span-1 md:col-span-2 flex justify-between text-sm">
                  <div className="flex items-center">
                    <Sun className={`mr-1 h-4 w-4 ${theme.darkMode ? 'text-amber-400' : 'text-amber-500'}`} />
                    <span>日出: {formatTime(weather.sunrise)}</span>
                  </div>
                  <div className="flex items-center">
                    <Sunset className={`mr-1 h-4 w-4 ${theme.darkMode ? 'text-amber-500' : 'text-amber-600'}`} />
                    <span>日落: {formatTime(weather.sunset)}</span>
                  </div>
                  <div className="flex items-center">
                    <div className={`h-3 w-3 rounded-full mr-1 ${weather.uvi < 3 ? 'bg-green-500' : weather.uvi < 6 ? 'bg-yellow-500' : weather.uvi < 8 ? 'bg-orange-500' : 'bg-red-500'}`}></div>
                    <span>UV: {weather.uvi < 3 ? '低' : weather.uvi < 6 ? '中' : weather.uvi < 8 ? '高' : '極高'}</span>
                  </div>
                </div>
              </div>
            )
          ) : (
            /* 7-day forecast display */
            forecast && (
              <div>
                {/* Compact forecast overview */}
                <div className="overflow-x-auto mb-4">
                  <div className="flex space-x-2 min-w-max">
                    {forecast.map((day, idx) => (
                      <button
                        key={idx}
                        onClick={() => setActiveForecastDay(idx)}
                        className={`flex flex-col items-center p-2 rounded-lg min-w-16 ${
                          activeForecastDay === idx 
                            ? (theme.darkMode ? 'bg-blue-700' : 'bg-blue-500 text-white') 
                            : (theme.darkMode ? 'bg-blue-900 bg-opacity-40' : 'bg-blue-50')
                        }`}
                      >
                        <div className="text-xs">{idx === 0 ? '今天' : formatDate(day.date)}</div>
                        {day.icon && (
                          <img 
                            src={`http://openweathermap.org/img/wn/${day.icon}.png`} 
                            alt={day.condition}
                            className="w-8 h-8"
                          />
                        )}
                        <div className="text-xs font-semibold">{Math.round(day.tempMax)}°</div>
                        <div className="text-xs opacity-75">{Math.round(day.tempMin)}°</div>
                      </button>
                    ))}
                  </div>
                </div>
                
                {/* Detailed view of selected day */}
                {forecast && forecast[activeForecastDay] && (
                  <div className={`${theme.darkMode ? 'bg-blue-900 bg-opacity-30' : 'bg-blue-50'} p-4 rounded-lg`}>
                    <div className="flex justify-between items-center mb-2">
                      <button 
                        onClick={prevForecastDay} 
                        disabled={activeForecastDay === 0}
                        className={`p-1 rounded-full ${activeForecastDay === 0 ? 'opacity-30' : 'hover:bg-blue-200'}`}
                      >
                        <ChevronLeft className="h-5 w-5" />
                      </button>
                      
                      <div className="font-semibold">
                        {activeForecastDay === 0 ? '今天' : formatDate(forecast[activeForecastDay].date)}
                      </div>
                      
                      <button 
                        onClick={nextForecastDay} 
                        disabled={activeForecastDay === forecast.length - 1}
                        className={`p-1 rounded-full ${activeForecastDay === forecast.length - 1 ? 'opacity-30' : 'hover:bg-blue-200'}`}
                      >
                        <ChevronRight className="h-5 w-5" />
                      </button>
                    </div>
                    
                    <div className="grid grid-cols-2 md:grid-cols-4 gap-3 mb-2">
                      <div className="flex items-center">
                        <Thermometer className={`mr-2 h-4 w-4 ${theme.highlight}`} />
                        <div className="text-sm">最高: {Math.round(forecast[activeForecastDay].tempMax)}°C</div>
                      </div>
                      <div className="flex items-center">
                        <Thermometer className={`mr-2 h-4 w-4 ${theme.highlight}`} />
                        <div className="text-sm">最低: {Math.round(forecast[activeForecastDay].tempMin)}°C</div>
                      </div>
                      <div className="flex items-center">
                        <Wind className={`mr-2 h-4 w-4 ${theme.highlight}`} />
                        <div className="text-sm">風速: {forecast[activeForecastDay].windSpeed} m/s</div>
                      </div>
                      <div className="flex items-center">
                        <Droplets className={`mr-2 h-4 w-4 ${theme.highlight}`} />
                        <div className="text-sm">降雨機率: {Math.round(forecast[activeForecastDay].pop * 100)}%</div>
                      </div>
                    </div>
                    
                    <div className="flex justify-between text-xs">
                      <div className="flex items-center">
                        <Sun className={`mr-1 h-4 w-4 ${theme.darkMode ? 'text-amber-400' : 'text-amber-500'}`} />
                        <span>日出: {formatTime(forecast[activeForecastDay].sunrise)}</span>
                      </div>
                      <div className="flex items-center">
                        <Sunset className={`mr-1 h-4 w-4 ${theme.darkMode ? 'text-amber-500' : 'text-amber-600'}`} />
                        <span>日落: {formatTime(forecast[activeForecastDay].sunset)}</span>
                      </div>
                    </div>
                  </div>
                )}
              </div>
            )
          )}
        </>
      )}
    </div>
  );
};

export default WeatherWidget;
