// weatherWidget.js 修改
import React, { useState, useEffect } from 'react';
import { CloudSnow, Wind, Droplets, Thermometer } from 'lucide-react';

const WeatherWidget = ({ theme }) => {
  const [weather, setWeather] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchWeather = async () => {
      try {
        setLoading(true);
        const apiKey = process.env.REACT_APP_OPENWEATHER_API_KEY;
        
        // 使用pro端點而不是api端點
        const response = await fetch(
          `https://pro.openweathermap.org/data/2.5/weather?lat=64.1466&lon=-21.9426&units=metric&appid=${apiKey}`
        );
        
        console.log("API響應狀態:", response.status);
        
        if (!response.ok) {
          throw new Error(`天氣API錯誤: ${response.status}`);
        }
        
        const data = await response.json();
        console.log("天氣數據:", data);
        
        setWeather({
          temperature: data.main.temp,
          condition: data.weather[0].description,
          icon: data.weather[0].icon,
          windSpeed: data.wind.speed,
          humidity: data.main.humidity,
          feelsLike: data.main.feels_like
        });
        
        setError(null);
      } catch (err) {
        console.error("獲取天氣失敗:", err);
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchWeather();
  }, []);

  return (
    <div className={`mb-6 ${theme.cardBg} rounded-xl p-4 ${theme.cardBorder}`}>
      <div className="flex justify-between items-center mb-3">
        <div className="flex items-center gap-2">
          <CloudSnow className={`h-5 w-5 ${theme.highlight}`} />
          <h2 className="text-lg font-semibold">雷克雅維克天氣</h2>
        </div>
      </div>
      
      {loading ? (
        <div className="text-center py-4">
          <div className="animate-spin h-8 w-8 border-2 border-current rounded-full border-t-transparent mx-auto"></div>
          <p className="mt-2">正在獲取天氣資料...</p>
        </div>
      ) : error ? (
        <div className="text-center py-4">
          <p className={`${theme.darkMode ? 'text-red-300' : 'text-red-600'}`}>
            {error}
          </p>
          <p className="mt-2 text-sm">請稍後再試或查看官方天氣預報</p>
        </div>
      ) : weather && (
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div className="flex items-center">
            {weather.icon && (
              <img 
                src={`http://openweathermap.org/img/wn/${weather.icon}@2x.png`} 
                alt={weather.condition}
                className="w-16 h-16"
              />
            )}
            <div>
              <div className="text-3xl font-bold">{Math.round(weather.temperature)}°C</div>
              <div className="capitalize">{weather.condition}</div>
            </div>
          </div>
          
          <div className="grid grid-cols-2 gap-2">
            <div className="flex items-center">
              <Wind className={`mr-2 h-4 w-4 ${theme.highlight}`} />
              <div className="text-sm">風速: {weather.windSpeed} m/s</div>
            </div>
            
            <div className="flex items-center">
              <Droplets className={`mr-2 h-4 w-4 ${theme.highlight}`} />
              <div className="text-sm">濕度: {weather.humidity}%</div>
            </div>
            
            <div className="flex items-center">
              <Thermometer className={`mr-2 h-4 w-4 ${theme.highlight}`} />
              <div className="text-sm">體感: {Math.round(weather.feelsLike)}°C</div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default WeatherWidget;
